\def\shift{4.5cm,-4.5cm}  
% We have 3 coordinates (1,2) and (2,3)
% so the shift is going to be (2,3) - (1,2) = (3,1)
% The result is going to be (-4,-3)  (1,2) - (3,1) = (-2,3)
% so the final shift is (-4,-3) - (-2,3) = (2,-3)

% The following array contains the names of the 7 days
% we have to define 1 day at the beginning (day 0) 
% then we make 6 other days with our shift (1,2) - (3,1) = (-4,-3)
% we assign colors to each day so that the result of the shift is 
% shown at the bottom of the box

\def\days{{"Saturday","Sunday","Monday","Tuesday","Wednesday","Thursday","Friday"}}

\def\shift{4.5cm,-4.5cm} 
\def\weekshift{1cm} % shift of the day before the week
% 5 is the number of days in the week

\begin{scope}[every node/.append style={alias=general,% general box
        path picture={\fill[blue!15] (path picture bounding box.north) -| (path picture bounding box.south east) -| (path picture bounding box.west) |- (path picture bounding box.north);}}] 

\foreach \i in {0,...,4}{
    \node[box] at ($(general.west)+(\i*\weekshift,0)$) (D\i) {\days[\i]};
    \coordinate (A\i) at ($(general.west)+(\i*\weekshift,\shift)$);
    \coordinate (B\i) at ($(general.east)+(\shift,\i*\weekshift)$);
    \draw[red,thick] (D\i.north west) |- (A\i) -- (B\i);
}

% Now we create the box at the center of the week
\node[box,anchor=south west,shift={(-\weekshift,\shift)}] (W) at (D4.south east) {\days[4]};
\coordinate (A) at (W.north east);
\coordinate (B) at ($(W.east)+(\shift,\shift)$);
\draw[red,thick] (W.north west) |- (A) -- (B);
\end{scope}

\draw[latex-latex,red,thick] (D3.north west) -- (W.north west)
node[midway,above]{Shifted by 1 day};

\end{tikzpicture}

\end{document}

