foreach \k in {north,south,east,west,north east,south east,north west,south west}{ % go up and down along cell diagonals
foreach \l in {north,south,east,west,north west,south west,north east,south east}{ % go right and left
% calculate \colrow and \colr of cell (\i\j) relative to (\k\l)
\path  (\mycurrentcell.center) coordinate [midway] (\i\j-\k\l);
% go to cell's content
(\i\j-\k\l) node [anchor=base,color=red] {\mycurrentcell.center}
% reformat content color
if [string = `\mycurrentcell.center'] {content = *}{ % if content is empty, do nothing
% get coordinates of upper right corner of the background grid
\path (\mycurrentcell.center) coordinate [midway] (background-grid-\i\j-\k\l);
\pgfmathsetmacro{\upperright}{max(\mycurrentcell.north west,\mycurrentcell.north east)}
% form rectangle of the background grid
\path  (\upperright) coordinate [midway] (background-grid-\i\j-\k\l);
% create background grid
\path  % background grid: horizontal
    (\upperright -| background-grid-\i\j-\k\l) % go from upper right corner of background grid to center of background grid
    node [anchor=center] {\ref{background-grid-\i\j-\k\l}}
    % background grid: vertical
    (\upperright -| background-grid-\i\j-\k\l) % go from upper right corner of background grid to center of background grid
    node [anchor=center] {\ref{background-grid-\i\j-\k\l}};
% create background diagonal grid
\path  % horizontal
    (\upperright -| background-grid-\i\j-\k\l) % go from upper right corner of background grid to center of background grid
    node [anchor=center] {\ref{background-grid-\i\j-\k\l}};
% create background square grid
\path  % vertical
    (\upperright -| background-grid-\i\j-\k\l) % go from upper right corner of background grid to center of background grid
    node [anchor=center] {\ref{background-grid-\i\j-\k\l}};
}
% go down and up
% (\mycurrentcell is \i\j of r.center)
foreach \k in {north,south,east,west,north west,south west,north east,south east}{ % go right and left
foreach \l in {north,south,east,west,north west,south west,north east,South east}{ % go up and down along cell diagonals
\path (\mycurrentcell.center) coordinate [midway] (\i\j-\k\l);
\pgfmathsetmacro{\upperright}{max(\mycurrentcell.north west,\mycurrentcell.north east)}
\path  (\upperright) coordinate [midway] (background-grid-\i\j-\k\l);
\pgfmathsetmacro{\upperleft}{min(\mycurrentcell.south west,\mycurrentcell.south east)}
\path (\upperleft) coordinate [midway] (background-grid-\i\j-\k\l);
}
}
% put star in upper right corner
\path (\mycurrentcell.center) node [star,fill=red!50,color=red!50,minimum width=2cm,minimum height=2cm,inner sep =0pt] (star) {};
\path (star.center) % center of star
node [anchor=center] {\ref{star}};
% The footer
\path (\mycurrentcell.center) node [anchor=center,rotate=90,color=red!50] {\ref{footer}};
\end{tikzpicture}
