% 8 squares around the perimeter
\def\squares{8,7,6,5,4,4,4,3}
\def\edge{2.5cm}

\foreach[count=\i] \j in \squares{
  % i-th square, j-th square
  \pgfmathsetmacro\posA{mod(\i-1,4)}
  \pgfmathsetmacro\posB{mod(\j+1,4)}
  \node[anchor=west, text width=2cm, font=\bfseries] at (\posA,\posB) (i) {\j};
}

% Perimeter
\draw[line width=2mm, red] (i) -- ++(\edge,0) node[right, red]{1}  -- ++(0,\edge) node[above, red]{2} -- ++(-\edge,0) node[left, red]{3} -- cycle;

% First line
\foreach[evaluate=\i as \j using int(\i+1)] \i in {2,5,8}{
  \draw[line width=2mm, green!80!black] (i) -- ++(0,\j) node[above, green!80!black]{4};
}

% Second line
\foreach[evaluate=\i as \j using int(\i+2)] \i in {3,4,5,6}{
  \draw[line width=2mm, blue] (i) -- ++(\j,0) node[right, blue]{5};
}

% Third line
\foreach[evaluate=\i as \j using int(\i+3)] \i in {4,7,8,9}{
  \draw[line width=2mm, orange] (i) -- ++(0,\j) node[above, orange]{6};
}

% Fourth line
\foreach[evaluate=\i as \j using int(\i+4)] \i in {1,2,3,4}{
  \draw[line width=2mm, purple] (i) -- ++(\j,0) node[left, purple]{7};
}


\end{tikzpicture}
