% Redefine page geometry
% 1  Pages are to be scaled
% 2  Rotate pages 90 deg
% 3  Page width and height are in cm/scale=1
% 4  Page numbers in red
\def\PageGeometry#1#2#3#4#5{%
  \par\vspace{-\baselineskip}%
  \parindent=0pt
  \noindent%
  \begin{tikzpicture}[x=\textwidth/0.914/#1, y=\textheight/0.865/#1]%
    % Text box
    \path[draw=red, thick, fill=yellow] (0,0) rectangle (#2,#3);
    % Redefine page numbers
    \foreach \i/\j in {0/0, 1/1, 2/0, 3/0, 4/1, 5/0, 6/0}%
    \node[anchor=south, font=\Large\bfseries\color{red}] at (\i*#2-0.5\pgflinewidth,#3-0.5\pgflinewidth) {\j};%
    % Scale content
    \begin{scope}[scale=#2, shift={(-#2*0.5\pgflinewidth,-#3*0.5\pgflinewidth)}, transform shape]
      \path[clip] (#4) rectangle (#5);
      \foreach \x/\y in #4 \path[draw=red, line width=4pt] (\x, \y)
        \foreach \u in {0,...,5} {
          -- ++(1, 0) coordinate (\x-\u)
        } -- ++(0, 1) -- ++(-1, 0) -- cycle;
      \foreach \x/\y in #4 \path[draw=red, line width=4pt] (\x, \y)
        \foreach \u in {0,...,5} {
          -- ++(1, 0) coordinate (\x-\u)
        } -- ++(0, 1) -- ++(-1, 0) -- cycle;
    \end{scope}%
  \end{tikzpicture}%
}

