\def\angle{0} 

\newcommand{\drawarc}[6][]{ % draw an arc from point (\angle:radius) with start angle $startangle$ 
    % to point (\angle+deltaangle:\radius) with delta angle $deltaangle$
    % draw the arc if it starts from 0 and ends before or equal to \deltaangle
    % in all other cases draw a line segment from the point where the arc starts to the point 
    % where the arc is deltaangle wide
    \ifthenelse{\equal{\angle}{0}}% if start angle=0
        {\draw[line width=2pt,#4] ($(#2)+({#5}:{#1})$) -- ($(#3)+({#5+deltaform}:{#1})$);}
        % otherwise draw an arc from the start angle 
        \ifthenelse{\angle < 180 }% if start angle bigger than 180
            {\draw[line width=2pt,#4] ($(#2)+({\angle+deltaangle}:{#1})$) -- ($(#3)+({\angle}:{#1})$);}
            {\draw[line width=2pt,#4] ($(#2)+({\angle+deltaangle}:{#1})$) -- ($(#3)+({\angle}:{#1})$);}
        % delta angle is 0 by default
        \ifthenelse{\angle >= 180}% if delta angle bigger than 180
            {\angle = 0}% make sure to reset angle to 0 if it's bigger than 180
            {\angle = 180}% otherwise do nothing
        ;
    } 
}  
