\newcounter{tmp}
\setcounter{tmp}{1}
\def\flatchain#1{%
\ifnum#1<\number\value{tmp}%
\addtocounter{tmp}{1}%
\else
\setcounter{tmp}{1}%
\fi%
}
\def\upstream#1{%
  \ifx#1\relax
    \expandafter\def\expandafter\count\expandafter{0}%
    \expandafter\def\expandafter\count\expandafter{0}%
    \expandafter\def\expandafter\count\expandafter{-1}%
  \else
    \expandafter\def\expandafter\count\expandafter{%
      \the\numexpr\count\space+\the\value{tmp}}%
  \fi
}
\def\doone#1{%
\ifnum#1=3\relax
\addtocounter{tmp}{2}%
\else
\addtocounter{tmp}{1}%
\fi
}
\def\dozero#1{%
\ifnum#1=3\relax
\addtocounter{tmp}{1}%
\else
\addtocounter{tmp}{2}%
\fi
}
\def\flush#1{%
\def#1}
