\NewDocumentCommand \Drunz { O{} m } {%
  \begin{tikzpicture}[#1]%
    \savebox\measuredSize{\Large\sffamily\bfseries Drunz}
    \begin{scope}[local bounding box=Drunz]
      \def\myscale{0.6}
      %\pgfmathsetmacro\myscale{0.6} % <<<--- put the common scaling factor here
      \def\scale{0.6}
      \def\H{5} % height of the main frame
      \def\W{5} % width of the main frame
      \def\height{3} % height of the infobox
      \def\width{3} % width of the infobox
      \def\a{0.6}
      \def\b{0.375} % added to prevent the text from colliding into the picture
      %\def\scale{2}
      \def\rectanglePath{%
        (0,0) % bottom left corner
        -- (.5*\W,0)
        -- (.5*\W,.\H)
        -- (0,.\H)
        -- (-.5*\W,.\H)
        % continue to the next segment
        -- ++ (0,-\b)
        -- ++ (-\a,0)
        -- ++ (0,0.5*\width)
        -- ++ (-\W,0)
        % continue from where we left off
        -- ++ (0,-\b)
        -- ++ (0.5*\W,0)
        -- ++ (0,-0.5*\W)
        -- ++ (0,-0.5*\width)
        -- ++ (0,\b)
        -- ++ (\a,0)
        -- ++ (0,\H)
        % continue to the top
        -- ++ (0,0.5*\height)
        -- ++ (-\W,0)
        % continue to the last part
        -- ++ (0,-\b)
        -- ++ (\a,0)
        -- ++ (0,0.5*\width)
        -- ++ (0,\b)
        -- ++ (-\W,0)
        -- cycle
      }
      % draw the box
      %\draw[black, line width=2pt] (0,0) -- (\W,0) -- (\W,3) -- (0,3) -- (0,0);
      \draw[line width=2pt] (0,0) rectangle (\W,3);
      % add the infobox
      \node (Infobox) [draw=red,
        fill=red!10,
        minimum width=\width,
        minimum height=\height,
        anchor=south west] at (.5*\W,\H) {};
      % add the text
      \node [align=center,font=\bfseries\sffamily] (Text) at (.5*\W,\H/2) {#2};
      % add the Drunz label
      \draw (Drunz.south west) to[out=180, in=270]
        node [below right, font=\sffamily] {Drunz #1}
        (Drunz.north west);
      % draw the actual rectangle
      \draw[red, line width=2pt, rounded corners=10pt] \rectanglePath;
      % add the title
      \node (Title) at (.5*\W,.5*\height) {#3};
      % draw the tick marks and labels
      \foreach \Tick in {0, 2, 4, 6}
      {
        \draw[black, line width=1pt] (#1,-\Tick) -- (#1,\Tick)
          node [anchor=south] at (Drunz.north, -\Tick) {\pgfmathparse{int(\Tick+1)}\pgfmathresult};
        \draw[black, line width=1pt] (-\Tick,#1) -- (\Tick,#1)
          node [anchor=west] at (-\W/2, #1) {\pgfmathparse{int(\Tick+1)}\pgfmathresult};
      }
    \end{scope}
    %\begin{pgfonlayer}{background}
