}


\newcommand\segments[2][]{%
  \def\noitems{0}%
  \def\initialshift{0pt}%
  \pgfmathtruncatemacro\segmentsno{#1}%
  \foreach \x [evaluate=\x as \segmentsno (initially \x)] in {1,...,\segmentsno} do {%
    \pgfmathtruncatemacro\thisshift{\x*#2}
      \xdef\noitems{\thisshift}%%
      \pgfmathsetmacro\xshift{\noitems*#2}%
      \path[bar] (top-\noitems.south west) 
        +(#2*\noitems pt, 0) coordinate (top-\noitems) 
        ++(\initialshift : \xshift) 
        coordinate (right-\noitems);
      \path[bar] (bottom-\noitems.south west) 
        +(-#2*\noitems pt, 0) coordinate (bottom-\noitems)
        ++(\initialshift : \xshift) 
        coordinate (left-\noitems);
  }
  \xdef\initialshift{\xshift}%
  \foreach \x [evaluate=\x as \xshift using \x*\initialshift/#2] in {1,...,\noitems} do {%
    \path[bar] (right-\x.east)
      ++(\xshift,0) coordinate (right-\noitem);
    \path[bar] (left-\x.west)
      ++(-\xshift,0) coordinate (left-\noitem);
  }
}


