    \node[text=white] at ($(A)!0.5!(D)$) {#4};% Shifted so that it is in the corner
    \node[rotate=45,scale=2] at ($(A)!0.5!(C)$) {#1};% 45 degrees | 1 | Green
    \node[rotate=-45,scale=2] at ($(D)!0.5!(C)$) {#2};% -45 degrees | 1 | Red
    \end{tikzpicture}
}

\newcounter{boxcount}
\newcommand{\boxes}[2]{%
\setcounter{boxcount}{1}%
\begin{tikzpicture}[baseline=(current bounding box.center)]
    \edef\maxboxes{#2}
    \pgfmathsetmacro{\cellsize}{1.1}
    \foreach \number in {1,...,\maxboxes}{
        \pgfmathsetmacro{\cellx}{\number-1}
        \pgfmathsetmacro{\celly}{\number-1}
        \ifnum \number<\maxboxes
         \def\CONTENT{}% so that at the end of the foreach there is nothing in the box
         \foreach \letter in {A,...,Z}
             {
             \setbox\box(\cellx,\celly)=\hbox{\boxbox{\letter}{\letter}}
             \expandafter\xdef\csname CONTENT\endcsname{\box\box{\letter}{\letter}}
             }
         \fi
        }
        \box[]{\maxboxes}{\maxboxes}
}%

