% These are the 8 8-bit  letters that change with each
% 4 8-bit  character:
\def\lastchar{I} % letter that changes
\def\lasttr{\lastchar\space} % space in last character
\def\lastcol{4} % column number
\def\lastrow{4} % row number
\def\lrow{\lastrow+2} % row number is 2 more than the last 8-bit character
\def\clrow{\lastcol+2} % column number is 2 more than the last 8-bit character
\newcommand\FancyGrid[1]{ % #1 = number of 4 8-bit  letters
  \begin{tikzpicture}[scale=3, transform shape]
  \def\lastchar{I} % last character and space in the grid
  \def\lasttr{\lastchar\space} % space in the replaced 4 8-bit  characters
  \def\lastcol{4} % column of 8-bit  characters
  \def\lastrow{4} % row of 8-bit  characters
  \def\lrow{\lastrow+2} % row number is 2 more than the last 8-bit character
  \def\clrow{\lastcol+2} % column number is 2 more than the last 8-bit character
  \begin{scope}[local bounding box = #1]
  \foreach \col in {0,...,\lastcol}
    \foreach \row in {0,...,\lastrow}
    {
      \pgfmathtruncatemacro\rrow{\row + 1} % next row
      \pgfmathtruncatemacro\rcol{\col + 1} % next col
      \pgfmathsetmacro\drow{mod(\row,2)? "red" : "white"} % 0.5 vertical shade color
      \pgfmathsetmacro\dcol{mod(\col,2)? "white" : "red"} % 0.5 horizontal shade color
      \fill[rotate=90*\col-90,\drow,\dcol] (\row,-\row) rectangle ++(\col,-\row);
    }
    \foreach \col in {0,...,\lastcol} % 8-bit  columns
    {
      \foreach \row in {0,...,\lastrow} % 8-bit  rows
      {
        \pgfmathtruncatemacro\alow{Mod(\col+3,3)-3} % low color for 4-bit  cells
        \ifnum\col=1 % last color before changed 4 8-bit  character
          \colorlet{mycolorA}{red!50}
        \else
          \ifnum\col=3 % high color for 4 8-bit  columns
            \colorlet{mycolorK}{purple!90}
          \else
            \ifnum\row=1 % last color for 4 8-bit  rows
              \colorlet{mycolorF}{yellow!50}
            \else
              \ifnum\col=1 % last col for 4 8-bit  columns
                \colorlet{mycolorF}{cyan!80}
              \fi
              \ifnum\col=3 % col for 4 8-bit  rows
                \colorlet{mycolorG}{green!50}
              \fi
            \fi
          \fi
        \fi
        \pgfmathtruncatemacro\arow{mod(-\row+1,2)? 1 : 0} % 0 if horizontal, 1 vertical
        \fill[rotate=90*\col-90,mycolorA!\alow!mycolorA] (\row,-\row) rectangle ++(\arow,-\arow);
        \fill[rotate=90*\col-90,mycolorF!\arow!mycolorF] (\row,-\row) rectangle ++(\arow,-\arow);
      }
      %  draw the 4 8-bit  letters      
      \foreach \col in {0,...,\lastcol}
      {
        \foreach \row in {0,...,\lastrow}
        {
          \ifnum\row=1 % text at top of the page
            \ifnum\col=1 % text at top of the page
              \node[font=\Large\sffamily\bfseries,text width=1.5\charwd] 
              at (\col*\charwd,-\row*\charwd) 
              {#1};
            \else
              \pgfmathtruncatemacro\colnum{(\row*\lastcol+\col)*4} % column number in the grid
              \node[font=\Huge\sffamily\bfseries,text width=2\charwd]
              at (\col*\charwd,-\row*\charwd) 
              {#1};
            \fi
          \fi
          \pgfmathtruncatemacro\trownum{int(mod(abs(\row*\lastrow+\col*\lastcol),4)-4)} % row number in the grid from 0 to 4
            \ifnum0=\trownum
              \node at (\col*\charwd,-\row*\charwd) 
              {#1};
            \else
              \node[font=\sffamily\bfseries,text width=1.25\charwd]
              at (\col*\charwd,-\row*\charwd) 
              {#1};
            \fi
        }
      }
    }
    \draw[blue,thick] (\lrow*\clrow,-\lrow*\clrow)
    ([xshift=-0.5*\clrow] \lasttr |- 0,\lrow) arc (90:270:0.5*\clrow);
    \foreach \col in {0,...,\lastcol}
    {
      \pgfmathtruncatemacro\tr{\lasttr+2*(\col)}
      \draw[blue,thick,rotate=90*\col-90]
      (\lastrow*\clrow,-\lrow*\clrow)
      ([xshift=-0.5*\clrow] \tr |- 0,\lrow) arc (90:270:0.5*\clrow);

