\def\x{.3} % screen width (you may want to restart the iteration and save the widths in a variable)
\def\y{3} % screen height

\foreach \i in {1,...,3}{% 3 grids in each direction
\foreach \j in {1,...,3}{% 3 rows in each direction
  \pgfmathparse{mod(\i+\j+1,2)}
  \ifnum\pgfmathresult=1 % display only the rightmost grid
    \ifnum\i=3
      \ifnum\j=3
        \pgfmathparse{Mod(floor(.5+\j/\y),4)}
        \ifnum\pgfmathresult=1 % display only the lower grid
          \begin{scope}[shift={(-\x,-\y)}]
            \header{1}{\i}{\j}{1};
            \header{1}{\i}{\j}{2};
          \end{scope} 
        \else
          \path[gray] (\i,-\y) rectangle ++(\x,-\y);
        \fi
      \fi
    \fi
  \fi
  \ifnum\j=3 % only display leftmost grid in second row
    \ifnum\i=3
      \pgfmathparse{Mod(floor(\j/\x),4)}
      \ifnum\pgfmathresult=1 % display only uppermost grid in second row
        \pgfmathparse{int(1-Mod(floor(\j/\x),4))}
        \ifnum\pgfmathresult=0 % only display leftmost grid in second row
          \begin{scope}[shift={(-\x,-\y)}]
            \header{2}{\i}{\j}{1};
            \header{2}{\i}{\j}{2};
          \end{scope} 
        \fi
      \fi
    \fi
  \fi
  \ifnum\i=3 % only display lowermost grid in first row
    \pgfmathparse{Mod(floor(\j/\x),4)}
    \ifnum\pgfmathresult=1 % display only leftmost grid in first row
      \begin{scope}[shift={(-\x,-\y)}]
        \header{1}{\i}{\j}{1};
        \header{1}{\i}{\j}{2};
      \end{scope} 
    \fi
  \fi
}
\draw[line width=2mm,red] (-\x,-\y) rectangle ++(2*\x,-2*\y);
}
