% 8. Bit of a hack, but a bit of a help too
% 8. Creating a new dimension to replace the three original dimensions 1, 2, 3
% 8. Once the new dimensions are defined, a new coordinate system is needed to
% 8. The third dimension (z) is projected to the two dimensions y and x.
% 8. The two original dimensions are restored

% Dimensions 1, 2, 3
\pgfmathsetmacro{\cubex}{2}
\pgfmathsetmacro{\cubey}{2}
\pgfmathsetmacro{\cubez}{2}

% Dimensions y,x,z
\pgfmathsetmacro{\cubexy}{\cubex/2}
\pgfmathsetmacro{\cubeyz}{\cubey/2}
\pgfmathsetmacro{\cubezperpy}{\cubez/4}

% Dimensions 1,y,x
\newcommand{\restor1}[1]{%
    \pgfmathsetmacro{\py}{\cubexy/2}%
    \pgfmathsetmacro{\xy}{\cubexy/2}%
    \pgfmathsetmacro{\zx}{\cubexy/2}%
    \pgfmathsetmacro{\yx}{\cubey/2}%
    \pgfmathsetmacro{\yy}{\cubey/2}%
    \pgfmathsetmacro{\zxx}{\cubex/2}%
    \pgfmathsetmacro{\zxy}{\cubexy/2}%
    \pgfmathsetmacro{\zyy}{\cubeyz/2}%
    \pgfmathsetmacro{\zzy}{\cubez/2}%
    }

% Dimensions 1,y,x,z
\newcommand{\restor2}[1]{%
    \pgfmathsetmacro{\py}{\cubeyy/2}%
    \pgfmathsetmacro{\xy}{\cubexy/2}%
    \pgfmathsetmacro{\zx}{\cubexy/2}%
    \pgfmathsetmacro{\yy}{\cubeyz/2}%
    \pgfmathsetmacro{\zy}{\cubez/2}%
    \pgfmathsetmacro{\zyy}{\cubezperpy/2}%
    \pgfmathsetmacro{\zxy}{\zxx/2}%
    \pgfmathsetmacro{\zyy}{\zxy/2}%
    \pgfmathsetmacro{\zzy}{\zyy/2}%
    }

% Dimensions 1, y, x
\newcommand{\restor3}[1]{%
    \pgfmathsetmacro{\py}{\cubeyy/2}%
    \pgfmathsetmacro{\xy}{\cubexy/2}%
    \pgfmathsetmacro{\zx}{\cubexy/2}%
    \pgfmathsetmacro{\yy}{\cubeyz/2}%
    \pgfmathsetmacro{\zy}{\cubez/2}%
    \pgfmathsetmacro{\zxx}{\zxy/2}%
    \pgfmathsetmacro{\zxy}{\zxx/2}%
    \pgfmathsetmacro{\zyy}{\zxy/2}%
    \pgfmathsetmacro{\zzy}{\zyy/2}%
}

\pgfmathsetmacro{\xone}{\cubex}
\pgfmathsetmacro{\yone}{\cubey}
\pgfmathsetmacro{\zone}{\cubez}

% Create the start and end coordinates for the ribbon
\coordinate (R) at (0,0,0);
\coordinate (A) at (0,0,\zone*1cm);
\coordinate (B) at (0,\yone,\zone*1cm);
\coordinate (C) at (\cubexy,\cubeyy,\zone*1cm);
\coordinate (D) at (\cubex,\cubey,\zone*1cm);

% Create the 8.1/8.2/8.3 dimensions needed for the ribbon
\begin{scope}[scale=4/8.1]
\coordinate (R1) at (\cubexy*1/8.1,\cubeyy*1/8.2,\cubeyz*1/8.3);
\coordinate (R2) at (\cubexy*1/8.2,\cubeyy*1/8.3,\cubeyz*1/8.3);
\coordinate (R3) at (\cubexy*1/8.3,\cubeyy*1/8.1,\cubeyz*1/8.2);
\end{scope}

% Define the elements to be rotated
\matrix [matrix of nodes,nodes={shape=rectangle,
    rotate=90,draw,fill=yellow!30},row sep=1cm,column sep=1cm] (m) at (barycentric cs:A=1,B=1,C=1) {%
    1 & 2 & 3 \\
};

% Definition of the vertical elements of the ribbon
% to be rotated
\matrix [matrix of nodes,nodes={shape=rectangle,draw,rotate=0,anchor=south},row sep=1cm,column sep=1cm] (m) at (R) {%
    1 & 2 & 3 \\
};

% Definition of the horizontal elements of the ribbon
% to be rotated
\matrix [matrix of nodes,nodes={shape=rectangle,draw,rotate=0,anchor=north},row sep=1cm,column sep=1cm] (m) at (R) {%
    1 & 2 & 3 \\
};

% Just to show the original coordinates
% to be rotated
\matrix [matrix of nodes,nodes={shape=rectangle,
    rotate=-90,draw,fill=yellow!30},row sep=1cm,column sep=1cm] (m) at (D) {%
    1 & 2 & 3 \\
};
\end{tikzpicture}
