\newcommand\Stair[2]{% #1 = path command, #2 = angle
\begin{tikzpicture}[scale=\scale, transform shape]
  \draw[thick,blue,fill=BLUE!30] (0,0) -- (0,6) -- (#1,6) -- (#1,0)-- cycle;
\end{tikzpicture}%
}

\newcounter{Line}
\setcounter{Line}{0}

\newcommand\Line[1]{% 
\stepcounter{Line} \ifnum\value{Line}>1
\foreach \Y [evaluate=\Y as \X using {int((\Y-1)/3)} ] in {1,...,3}
   \draw[thick,blue!70] (\theLine,6) |- ($(0,6)+(\X,\Y)$) |- (\theLine,0);
\fi%
}

