% We define the main style
% parameters: [scale, color]_1: number of months [1...360]
%              [scale, color]_2: number of children [1...4]
%              [scale, color]_3: amount of money to be invested [1...5]
%              [scale, color]_4: percentage of children to be invested [1...4]
%              [scale, color]_5: date of the last child [yyyy-mdd]
\newcommand*{\child}[6][0.5]{%
    \begin{tikzpicture}
        \def\width{#3}%
        \def\height{#4}%
        \node[%
            trapezium,%
            trapezium left angle=65,%
            trapezium right angle=115%
        ] at (0,0) %
            {\textbf{#2} Children #6};
        \node (sender) at (\width pt,-\height pt) {};
        \fill [color=Color1] (sender) rectangle +(-.1,-.1);%
        \fill [color=Color2] (sender) rectangle +(\width,.1);%
        \fill [color=Color3] (sender) rectangle +(-.1,.1);%
        \fill [color=Color4] (sender) rectangle +(\width,-.1);%
        \fill [color=Color5] (sender) rectangle +(-.1,-\height);%
        \node[%
            text width=\width cm,%
            scale=1.2,
            color=black,
            font=\bfseries,
            align=flush center,
            text width=3cm,
        ] at (\width/2,-\height/2-.3) {D};
        \node[%
            text width=\width cm,
            scale=1.2,
            color=black,
            font=\bfseries,
            align=flush center,
            text width=3cm,
        ] at (-\width/2,-\height/2-.3) {S};
        \draw[thick] ([xshift=-.5cm]sender) coordinate (x) -- 
            (x |- today) coordinate (y);
        \IfBooleanT{#1}{%
            \draw [ultra thick, blue, -] (y) -| 
                (1,-\height/2) node [below, black, scale=.9] {%
                \begin{tabular}{@{}r@{ }l@{}}
                \begin{LARGE}
                    \vdots
                \end{LARGE}
                \end{tabular}%
            };
        }%
        \draw [thick, red, -] (y) -| 
            (0,-2) node [above, black, scale=.9] {%
                \begin{tabular}{@{}r@{ }l@{}}
                \begin{LARGE}
                    \vdots
                \end{LARGE}
                \end{tabular}%
            };
        %
        \IfBooleanT{#2}{%
            \draw [thick, blue, -] (y) -| (-\width/2,-2) node [above left, black, scale=.9] {%
                \begin{tabular}{@{}l|r}
                \color{white} \shortstack{%
                  \underline{%
                  Amount \newline to be invested} \\
                  $
                  ->%
                  $%
                  \num{#3}%
                }%
                \\
                %
                \underline{\textbf{#5}}%
                %
                Child \newline count=\textbf{#6}\\
                \end{tabular}%
            };
        }%
        \draw [thick, blue, -] ([xshift=.5cm]y) coordinate (y) -- 
            (y -| 3,0) coordinate (z);
        \node [right, draw, fill=white, align=flush right] at ([xshift=.5cm, yshift=.3cm]z) {%
            \begin{tabular}{@{}r@{ }l@{}}
            \begin{LARGE}
                \vdots
            \end{LARGE}
            \end{tabular}%
        };
    }
}
\newcounter{Row}
\newcommand{\numChild}[1]{%
    \stepcounter{Row}%
    \begin{tikzpicture}
        \path (0,0) node (sender) {} ++(1.5\width,0) coordinate (x)
            ([xshift=-.5cm]x) coordinate (y) -- (y |- today) coordinate (z)
                ++(-.1,-.1) coordinate (x);
        \IfBooleanT{#1}{%
            \draw [thick, blue, -] (y) -| 
                (1,-\height/2) node [below, black, scale=.9] {%
                \begin{tabular}{@{}r@{ }l@{}}
                \begin{LARGE}
                    \vdots
                \end{LARGE}
                \end{tabular}%
            };
        }%
        \draw [thick, red, -] (y) -| (-\width/2,-2) node [above left, black, scale=.9] {%
            \begin{tabular}{@{}l|r}
            \color{white} \shortstack{%
                \underline{%
                Amount \newline to be invested} \\
                $
                ->%
                $%
                \num{#1}%
            }%
            \\
            %
            \underline{\textbf{#3}}%
            %
            Child \newline count=\textbf{#6}\\
            \end{tabular}%
        };
    \end{tikzpicture}%
    \stepcounter{Row}%
    \ifnum\value{Row}=1
    \newpage
    \else
    \ifnum\value{Row}=12
    \newpage
    \fi
    \fi
}

