 \global\edef\myz{#3} % distance from front of cylinder to back of cylinder
 \pgfmathparse{int(#4)} \global\let\myn=\pgfmathresult
 \pgfmathparse{int(#5)} \global\let\mym=\pgfmathresult
 \pgfmathsetmacro{\myangle}{90/\myn*1/\myz}
 }
}
