  \pagecolor{black}
  \begin{tikzpicture}[>=stealth,baseline={(0,-0.5)},shift={(0,2)},scale=0.5]
    \begin{scope}[cylinder length=\myz, cylinder width=1.5]
      \pgfmathtruncatemacro{\nmax}{\myn}
      \foreach \x in {1,...,\nmax}
      {
        \pgfmathparse{mod(\x+\z+1,\myn)? "red" : "black"}
        \colorlet{boxcolor}{\pgfmathresult}
        \draw[boxcolor] (0,\myz) rectangle ++(1.5,1);
        \cylinder{\x}{\myz}{\myz}{\x}
      }
      \pgfmathtruncatemacro{\nextcyl}{mod(\z+1,\myn)}
      \ifnum\z<11 \foreach \i in {0,...,3}{%
          \ifnum\i=1\else
            \pgfmathparse{mod(\nextcyl+1-\i,\myn)? "red" : "black"}
            \colorlet{boxcolor}{\pgfmathresult}
            \draw[boxcolor] (\myz+1.5*\i:\myz) 
            ++(${2*\myz/4}*(rand-0.5,1)$) 
            to[bend left] ++(${2*\myz/4}*(1,-rand+0.5)$) 
            to[bend right] ++(${2*(\myz/2)}*(rand-0.5,1)$) 
            to[bend left] ++(${2*(\myz/4)}*(1,-rand+0.5)$) 
            to[bend right] cycle;
          \fi
      }%
      \global\let\prevcyl=\cyl
      \global\let\nextcyl=\cyl
      \foreach \i in {0,...,3}{
        \pgfmathparse{mod(\nextcyl+\i+\z+1,\myn)? "red" : "black"}
        \colorlet{boxcolor}{\pgfmathresult}
        \draw[boxcolor] (\myz+1.5*\i:\myz) 
        ++(${2*\myz/4}*(rand-0.5,1)$) 
        to[bend left] ++(${2*(\myz/2)}*(rand-0.5,1)$) 
        to[bend right] ++(${2*(\myz/4)}*(1,-rand+0.5)$) 
        to[bend left] ++(${2*(\myz/4)}*(1,-rand+0.5)$) 
        to[bend right] cycle;
      }
      \fill[black] circle(2pt);
      \pgfmathtruncatemacro{\retest}{mod(mod(\z+1,5)+1)}
