% root of the tree, taken from the document
\coordinate (root) at (0,0);

% First we draw the horizontal lines:
\foreach \i in {-1,1} {
  \draw[blue, thick]
    ($ (root) + (\i,0) $) -- +(0,-3);
}

% Then we draw the blue arrows. They need n1 to be -1 or 1
% in order to render correctly in an node. But n1 is not used in
% the drawing so it can be made to do nothing in that case
\foreach \f in {1,n1} {
\draw[blue, <->] 
    ($ (root) + (0,\f) $) -- +(2,0) node[right] {n1};
}

% Finally we draw the red arrows. They need to be of the same length
% as the blue arrows in the previous case but are not using n1
% in order to render correctly.
\foreach \s in {-1,1} {
\draw[red, -] 
    ($ (root) + (\s,3) $) -- +(2,0) node[right] {s1};
\draw[red, -] 
    ($ (root) + (\s,-3) $) -- +(2,0) node[right] {s2};
}

% Now we draw the red arrows that use n1. You can erase these
% arrows if n1 is taken out of the drawing
\foreach \f in {-1,1} {
\draw[red, <->] 
