\pgfdeclareshape{cylinder}{
  \inheritsavedanchors[from=rectangle]
  \inheritanchorborder[from=rectangle]
  \inheritanchor[from=rectangle]{north}
  \inheritanchor[from=rectangle]{north west}
  \inheritanchor[from=rectangle]{north east}
  \inheritanchor[from=rectangle]{center}
  \inheritanchor[from=rectangle]{west}
  \inheritanchor[from=rectangle]{east}
  \inheritanchor[from=rectangle]{mid}
  \inheritanchor[from=rectangle]{mid west}
  \inheritanchor[from=rectangle]{mid east}
  \inheritanchor[from=rectangle]{base}
  \inheritanchor[from=rectangle]{base west}
  \inheritanchor[from=rectangle]{base east}
  \inheritanchor[from=rectangle]{south}
  \inheritanchor[from=rectangle]{south west}
  \inheritanchor[from=rectangle]{south east}
  \backgroundpath{
     % store lower right in xa/ya and upper right in xb/yb
    \storein={\cornerA}{\cornerB}{\pgfpoint{\southwest}{\baselineskip}}
    % get the ray direction
    \pgfpointxy{(\cornerA-\cornerB)/2}{(\cornerA+\cornerB)/2}
    % get rotation angle
    \pgfmathsetmacro{\myrot}{atan2(\pgfpointy{\cornerA}-\pgfpointy{\cornerB},\pgfpointx{\cornerA}-\pgfpointx{\cornerB})}
    % get rotation angle (rounded to 2 digits)
    \pgfmathsetmacro{\myrot}{round(\myrot)}
    \pgfmathsetmacro{\myrot}{\myrot-90}
    % compute origin
    \pgfpointadd{\southwest}{\pgfpoint{1cm}{0pt}}
    \pgfpointadd{\northwest}{\pgfpoint{-1cm}{0pt}}
    \pgfpointadd{\northeast}{\pgfpoint{-1cm}{-1cm}}
    \pgfpointadd{\southwest}{\pgfpoint{1cm}{-1cm}}
    % get corner lengths
    \pgfmathsetlength{\pgf@x}{(\cornerA-\cornerB)/2}
    \pgfmathsetlength{\pgf@y}{(\cornerA+\cornerB)/2}
    \pgfmathsetlength{\pgf@z}{(\cornerA-\cornerB)/2}
    \pgfmathsetlength\pgf@x{\cornerA}
    \pgfmathsetlength\pgf@y{\cornerB}
    \pgfmathsetlength\pgf@z{\cornerA}
    % get corner size
    \pgfmathsetlength\pgf@x{\pgfkeysvalueof{/pgf/minimum width}}
    \pgfmathsetlength\pgf@y{\pgfkeysvalueof{/pgf/minimum height}}
    \pgfmathsetlength\pgf@z{\pgfkeysvalueof{/pgf/minimum height}}
    % now draw the thing
    \pgfpathmoveto{\pgfqpoint{0pt}{\pgf@y*\pgf@x}}
    \pgfpatharc{90}{-90}{\pgf@x}
    \pgfpathlineto{\pgfpointadd{\pgfqpoint{\pgf@x}{0pt}}{\pgfqpoint{-\pgf@z}{0pt}}}
    \pgfpathlineto{\pgfpointadd{\pgfpoint{-\pgf@x}{\pgf@z}}{\pgfpoint{0pt}{\pgf@y*\pgf@x}}}
    \pgfpathlineto{\pgfpointadd{\pgfpoint{-\pgf@z}{0pt}}{\pgfpoint{0pt}{-\pgf@y*\pgf@x}}}
    \pgfpathclose
    \pgfusepath{stroke}
  }
}

