\begin{tikzpicture}
\begin{axis}[
    legend style={at={(0.03,0.97)},anchor=north west},
    width=0.9\textwidth,
    height=0.35\textwidth,
    grid,
    no markers,
    every axis plot post/.append style={thick},
    axis x line=bottom,
    axis y line=left,
    xlabel={well depth (m)},
    ylabel={fluid volume (ms)},
    xlabel style={at={(1,0.8)}},
    ylabel style={at={(0.5,0.1)}},
    %xtick={0,0.2,...,1.5},
    xticklabel={\pgfmathparse{0.2*\tick}\pgfmathprintnumber{\pgfmathresult}},
    ytick={0,0.5,...,1},
    %ymajorgrids=true,
    grid style=dashed,
    samples=50,
    domain=0:1,
]
\addplot[name path global=well,blue,thick] {1/x};
\addplot[name path global=fof,green,thick] {1/x^2};
\addplot[name path global=sat,red,thick] {1/x^3};

% use the intersections library to calculate the intersection points of the
% three functions with the x-axis
\path [name intersections={of=well and fof,sort by=well,name=W,total=\t}]
    \pgfextra{\xdef\InterNb{\t}};
\path [name intersections={of=well and sat,sort by=sat,name=X,total=\t}]
    \pgfextra{\xdef\SatNb{\t}};

% Use the pgfplotsintersections library to calculate the intersection points of
% the function and the grid
\begin{pgfinterruptboundingbox}
    \path [name path global=grid] (\pgfkeysvalueof{/pgfplots/xmin},0) -- (\pgfkeysvalueof{/pgfplots/xmax},0);

    \foreach \X in {1,...,\InterNb}
        {
        % add the name of the intersection point to the path
        \path [name path global=\pgfkeysvalueof{/pgfplots/x-\X}-axis]
            (\pgfkeysvalueof{/pgfplots/x-\X},0) -- (\pgfkeysvalueof{/pgfplots/x-\X},\pgfkeysvalueof{/pgfplots/ymax});
            \draw [name intersections={of=\pgfkeysvalueof{/pgfplots/x-\X}-axis and grid,sort by=grid,name=I-\X,total=\t}]
            \pgfextra{\xdef\InterNbT{\t}};
        }

    % calculate the total intersection number, since we want the one with the
    % fewest intersections
    \pgfmathsetmacro{\InterNbT}{\InterNbT>\SatNb? \InterNbT : \SatNb}

    % now draw the axis along it's intersection
    \draw [name path global=x-axis,blue,dashed]
        (\pgfkeysvalueof{/pgfplots/xmin},0) -- (\pgfkeysvalueof{/pgfplots/xmax},0);
    \pgfinterruptboundingbox

    % the following two commands are from the PGFPlots manual
    \pgfplotsinvokeforeach {1,...,\InterNbT}
    {
        \draw [name intersections={of=x-axis and I-\X,sort by=I-\X,name=I-\X,total=\t}]
        \pgfextra{\xdef\InterNbT{\t}};
    }

    % the thick lines
    \draw [very thick,green!70!black,dashed]
        (I-1) -- (I-1|-0,0);
    \draw [very thick,red!70!black,dashed]
        (I-\InterNbT) -- (I-\InterNbT|-0,0);

    % the highlighted area
    \draw [thick,green!70!black,fill=green!20,rounded corners]
        (I-1|-0,0) rectangle (I-\InterNbT);

\end{axis}
\end{tikzpicture}
\end{document}

