  }

  % define cell environment
  \begin{scope}[local bounding box=cells]
    % define cell coordinates
    \definecoords{current}{current}
    \definecoords{cell1}{current1}
    \definecoords{cell2}{current2}
    \definecoords{cell3}{current3}
    \definecoords{cell4}{current4}
    \definecoords{cell5}{current5}
    \definecoords{cell6}{current6}
  \end{scope}
  % draw cells
  \begin{scope}[shift={(0,5)}]
    \foreach \x in {1,...,6} {
      \foreach \y in {1,...,3} {
        \draw[blue] (\x,\y) circle (0.2);
      }
    }
  \end{scope}
  % draw grid
  \draw[gray,thin] (current2 |- cell3) -- (cell3 |- cell6) 
                (current2 |- cell5) -- (cell5 |- cell6);

  % helper coordinates for relative coordinate system
  \def\curlyaxisrelshift{2,0}
  \def\curlyaxislabshift{2,0}

  % position helper coordinates
  \coordinate (current1) at ($(current1) + (0,\curlyaxislabshift)$);
  \coordinate (current2) at ($(current2) + (\curlyaxisrelshift,0)$);
  \coordinate (cell1) at ($(cell1) + (0,\curlyaxislabshift)$);
  \coordinate (cell2) at ($(cell2) + (\curlyaxisrelshift,0)$);
  \coordinate (cell3) at ($(cell3) + (\curlyaxislabshift,\curlyaxislabshift)$);
  \coordinate (cell4) at ($(cell4) + (0,\curlyaxislabshift)$);
  \coordinate (cell5) at ($(cell5) + (\curlyaxisrelshift,0)$);
  \coordinate (cell6) at ($(cell6) + (\curlyaxislabshift,\curlyaxislabshift)$);
  \coordinate (current3) at ($(current3) + (\curlyaxislabshift,\curlyaxislabshift)$);
  \coordinate (current4) at ($(current4) + (\curlyaxislabshift,\curlyaxislabshift)$);
  \coordinate (current5) at ($(current5) + (\curlyaxislabshift,\curlyaxislabshift)$);
  \coordinate (current6) at ($(current6) + (\curlyaxislabshift,\curlyaxislabshift)$);

  % draw helper coordinates in relative coordinate system
  \helper{current}{current1}
  \helper{current}{current2}
  \helper{current}{cell1}
  \helper{current}{cell2}
  \helper{current}{cell3}
  \helper{current}{cell4}
  \helper{current}{cell5}
  \helper{current}{cell6}

  % draw shifted grid
  \draw[blue] (current2 |- cell3) -- (cell3 |- cell6) 
                (current2 |- cell5) -- (cell5 |- cell6);

  \foreach \coord in {current,cell1,cell2,cell3,cell4,cell5,cell6} {
    \draw[red] ($(\coord) - (0,\curlyaxislabshift)$) -- ($(\coord) + (0,\curlyaxislabshift)$);
  }

  % relative coordinate system
  \draw[gray,thick] ($(current2) - (\curlyaxisrelshift,\curlyaxislabshift)$) 
    arc[start angle=90, end angle=180, radius=\curlyaxislabshift] 
    node[midway,anchor=south west,inner sep=0pt] {\texttt{\curlyaxislabshift}};
  \draw[gray,thick] ($(current3) - (0,\curlyaxislabshift)$) arc[start angle=0, 
    end angle=90, radius=\curlyaxislabshift] 
    node[midway,anchor=south west,inner sep=0pt] {\texttt{\curlyaxislabshift}};
  \draw[gray,thick] ($(current4) - (0,\curlyaxislabshift)$) arc[start angle=0, 
    end angle=90, radius=\curlyaxislabshift] 
    node[midway,anchor=south west,inner sep=0pt] {\texttt{\curlyaxislabshift}};
\end{tikzpicture}
\end{document}

