     \draw[red, ultra thick, rotate around={\midangle:(0,0)}] (0,0) -- (#3/2,#3/2) -- (#3/2,0) -- cycle;
    \else
     \draw[gray, ultra thick, rotate around={\midangle:(0,0)}] (0,0) -- (#3/2,#3/2) -- (#3/2,0) -- cycle;
