\newcommand{\countdown}[3]{% \countdown[<label>]{<initial countdown interval>}{<item number>}
  \pgfmathsetmacro{\mycountdown}{#2}%
  \pgfmathsetmacro{\mydistance}{#2-1}%
  \foreach \x in {0,...,\mydistance} {%
    \pgfmathsetmacro{\mylabel}{%
      \ifnum\the\pgf@count>0\relax
        \ifnum\the\pgf@count=1
          \expandafter\counttoc\expandafter{\mycountdown}{\x}
          \usepgfmathresult
        \else
          \x
        \fi
      \else
        0
      \fi
    }
    \xdef\@label{\csname pgf@label@\the\pgf@count\endcsname}
    \xdef\@temp{\noexpand\draw[style=\noexpand\draw] (0,0) -- ({\mycountdown-\the\pgf@count}*\mylabel,0);}
    \xdef\@temp{\@temp,\@temp}
    \xdef\@temp{\@temp  node [right, style={align=right, xslant=0}]{\@label}}
    \xdef\@temp{\@temp  node [yshift=2pt, style={align=right, xslant=0}]{\@label}}
  }
  \xdef\@temp{%
    \noexpand\draw[style=\noexpand\draw] (0,0) -- ({\mycountdown-\the\pgf@count}*\mylabel,0);}
  \@temp
}
\makeatother

