\tikzset{Marrow/.pic={
    % Save the number of petals in a variable
    \pgfmathsetmacro{\num petals}{0}
    % Loop through the number of petals
    \foreach \angle/\radius in {60/1.25, 90/1.35, 120/1.35, 150/1.25, 180/1.25, 210/1.35, 240/1.35, 270/1.25, 300/1.25, 330/1.25}
        \pgfmathsetmacro{\num petals}{\num petals+1}
    % Draw the outer circle and add markings at the right angles
    \draw[line width=1.5pt] (0, 0) circle (\radius);
    \foreach \angle in {0, 60,..., \num petals}
        {
            \filldraw (\angle:\radius) circle (2pt);
            \pgfmathsetmacro{\bigrad}{\angle + 90}
            \pgfmathsetmacro{\smallrad}{\radius - 2*0.4}
            \draw[line width=1.5pt] (\bigrad:\radius) -- (\smallrad:\radius);
        }
    % Draw the inner circle and add markings at the angles
    \foreach \angle in {0, 60,..., \num petals}
        {
            \filldraw (\angle:\radius) circle (2pt);
            \pgfmathsetmacro{\bigrad}{\angle + 90}
            \pgfmathsetmacro{\smallrad}{\radius - 0.5}
            \draw[line width=1.5pt] (\bigrad:\radius) -- (\smallrad:\radius);
        }
    }
}

