\documentclass{article}
\usepackage{tikz}
\begin{document}
\begin{tikzpicture}
% the points
\def\numpts{20}
\pgfmathsetmacro\n{sqrt(\numpts*4-5)/2}
\pgfmathsetmacro\circlefactor{2*sqrt(\numpts+1)/2}
\pgfmathsetmacro\labelfactor{2*sqrt(\numpts+3)/2}
\foreach \pt in {1,...,\numpts}
{
  \pgfmathsetmacro\angle{360*\pt/\numpts}
  \pgfmathsetmacro\radius{sqrt(4*\pt/(\pt+1))}
  \fill (\angle:\radius) circle (2pt) node[shift={(\angle+90:0.35)},circle,fill=black] {\pt};
}

% the lines
\foreach \pt in {1,...,\numpts}
{
  \pgfmathtruncatemacro\pti{mod(\pt,\numpts)+1}
  \pgfmathtruncatemacro\ptii{mod(\pt,\numpts)+1}
  \draw (\angle+\pt*360/\numpts-360:\radius) --
        node[auto,swap] {$w_{\pti}$} (\angle+\pt*360/\numpts-360:\radius/1.7) node{$s_\pt$};
  \draw (\angle+\pt*360/\numpts+360:\radius) --
        node[auto] {$w_\ptii$} (\angle+\pt*360/\numpts+360:\radius/1.7) node{$s_\ptii$};
}

% the labels
\foreach \pt in {1,...,\numpts}
{
  \pgfmathtruncatemacro\labelk{mod(\pt,\numpts)+1}
  \node at (\pt*360/\numpts-360*0.35+90:\circlefactor) {$E_{\labelk}$};
  \pgfmathtruncatemacro\labeln{mod(\pt,\numpts)}
  \node[auto,swap] at (\pt*360/\numpts-360*0.35+90:\labelfactor) {$G_{\labeln}$};
}

% the curve
\draw
  (\angle-360/\numpts:1.75*\radius) to[out=90,in=180]
  (\angle-360/\numpts:2.5*\radius) to[out=0,in=180]
  (\angle-360/\numpts:3.25*\radius) to[out=0,in=90]
  (1.75*\radius,0) to[out=-90,in=90]
  (\angle:3.25*\radius)
  (\angle:3.25*\radius) to[out=-90,in=0] cycle;
\end{tikzpicture}
\end{document}

