        \xdef\myx{\myx + 10*\i};
        \xdef\myy{\myy + 10*\i};
    };
    \cred = \myx * 10;
    \green = \myy * 10;
    \blue = \myx * 2 + \myy * 2;
    \yellow = \myx * 2 + \myy * 2 + 1;
    for \j in {0,..., 9}{%
        for \k in {0,..., 100}{%
            \typeout{test: x = \myx, y = \myy, c = \cred, r = \green, b = \blue, g = \yellow}
