\newcommand\mygraph[2]{%
% #1 = nodes
% #2 = list of node IAB labels
\begin{tikzpicture}[grow=right]
  \foreach \x/\lbl [count=\y] in {#2} {
    \ifnum\x>1
      \node[fill=yellow,circle] (N\x) at (\y*72:3) {\lbl};
    \else
      \node[fill=green,circle] (N\x) at (\y*72:3) {\lbl};
    \fi
    \node[fill=red!20,rounded corners,inner sep=20pt] at (N\x) {};
    \xdef\maxx{\y}
    \foreach \z in {1,...,\x} {
      \pgfmathtruncatemacro{\nnz}{\z+1}
      \draw[thick,-latex] (N\z.center) to [out=180,in=90] (N\nnz.center);
      \draw[thick,-latex] (N\nnz.center) -- (N\x.center) to [out=-90,in=0] node[below] {$\vec{#1}$} (N\x.center);
    }
  }
  \draw[thick,red,latex-] (N\maxx.center) to [out=45,in=-90] (N1.center);
  \foreach \x [count=\y] in {2,...,\maxx} {
      \pgfmathtruncatemacro{\nnx}{\x-1}
      \draw [red,-latex] (N\x.center) -- (N\nnx.center)
      node[xshift=20pt] {$\vec{#1}$};
  }
\end{tikzpicture}%
}

