            \pgfmathtruncatemacro{\X}{\i-10}
            \foreach \j in {1,...,\i}
            {   \draw (\j,\i) rectangle (\j+1,\i+1);
                \pgfmathtruncatemacro{\Y}{\i/2*(\i-10)*(\i-9)+\j-1}
                \node at (\j,\i+0.5) {\Y};
                \node at (\j+0.5,\i) {\Y};
            }
        \fi
        \ifnum \i>19
            \pgfmathtruncatemacro{\X}{\i-20}
            \foreach \j in {1,...,\i}
            {   \draw (\j,\i) rectangle (\j+1,\i+1);
                \pgfmathtruncatemacro{\Y}{\i/2*(\i-20)*(\i-19)+\j-1}
