\begin{tikzpicture}[line cap=round,line join=round,x={(-0.80cm,-0.25cm)},y={(0.80cm,-0.25cm)},z={(0cm,0.80cm)}]
  % dimensions
  \def\ra{2}    % radii of the arcs
  \def\rc{0.1}  % radius of the central point
  \def\ro{0.2}  % radius of the central point
  \def\rcol{1}   % scale factor for the central point
  \def\h{1.2}   % cylinder height (total height: \ra + \h)
  \def\th{0.4}   % semiellipses axis offset from the origin
  \pgfmathsetmacro\ax{\ra+\th} % semiellipses x offset
  \pgfmathsetmacro\ay{\h}   % semiellipses y offset
  \pgfmathsetmacro\be{\th+\ay} % arc of the semiellipses
  \pgfmathsetmacro\bh{\ra+\be} % arc of the semiellipses
  % style
  \tikzset
    {%
      cylinder fill/.style={%
        fill=blue!15,draw=blue,thick}
    }
  % the origin (cylinder bottom)
  \coordinate (O) at (0,0,0);
  \draw[thick,ycomb] plot[domain=-0.1:0.1,samples=20] ({\x},{0},{0});
  \draw[cyan] (O) -- ++(-\ra,0,0) -- ++ (0,\h,0) -- ++ (\ra,0,0);
  \draw[cyan,dashed] (O) -- ++ (0,0,\h);
  \shade[cylinder,fill=gray,opacity=0.2] (O) circle (\ra);
  \draw[thick,ycomb] plot[domain=-0.1:0.1,samples=20] ({\x},{\h},{0});
  % cylinder top
  \draw[cylinder,shape border rotate=90] (\ax,\ay,\bh) -- (\ax,\ay,\bh+\h)
    -- (\ax,\ay+\h,\bh) -- (\ax,\ay+\h,\bh-\h) -- cycle;
  \draw[cylinder,shape border rotate=90] (\ax,\ay,\bh-\h) -- ++ (0,0,\h);
  \shade[cylinder,fill=blue!60,opacity=0.2] (O) circle (\rc);
  % semiellipses
  \foreach\i in {-1,1}
    {%
      \begin{scope}[canvas is xy plane at z=\h+\i*\h]
        \draw[dashed,ycomb] plot[domain=-\ra:\ra,samples=20] ({\x},{\ra+\i*\h});
        \draw[line width=1mm] plot[domain=-\ra:\ra,samples=20] ({\x-\i*\h},{\ra+\i*\h});
        \fill[rotate=45] (-\ra-\i*\h/2,0) rectangle (\ra-\i*\h/2,\ra+\i*\h);
        \draw (\ra-\i*\h/2,0) arc (0:180:\ra-\i*\h/2);
        \fill[rotate=45] (0,\ra) ellipse (\ra-\i*\h/2 and 0.9*\ra-\i*\h/2);
      \end{scope}
    }
  % arc of the semiellipses
  \draw[line width=1mm,ycomb] plot[domain=\ra:\ro,samples=20] ({\x},0,{0});
  \fill[rotate=45] (0,0) ellipse (0.2 and 0.9);
  % arrows
  \draw[-latex] (O) -- ++ (0,0,\h) node [left]  {$\rho$};
  \draw[-latex] (O) -- ++ (\th,0,0) node [right] {T};
  \draw[-latex] (O) -- ++ (0,-0.3,0) node [right] {n$_{\uparrow}$};
  \draw[-latex] (O) -- ++ (0,0,-0.7) node [above] {n$_{\downarrow}$};
  \draw[-latex] (O) -- ++ (0.7,0,0) node [above] {$\vec{t}$};
  % labels
  \node [x={(0.8cm,0cm)},y={(-0.5cm,0.4cm)},z={(0cm,0.8cm)},inner sep=2pt] (t) at (\th/2,0,0) {$\begin{array}{c}\mbox{Transmitted  Beam} \\ \mbox{at }t\hbar\end{array}$};
  \shade[canvas is xy plane at z=\h+0.4] (t.south west) rectangle (t.north east);
\end{tikzpicture}
