% Relative to (0,0):
% width  : x direction of the first crossing 
% height: y direction of the first crossing 
\newcommand{\crossWidth}[2]{(#1,#2) -- (#1-1,#2)}
\newcommand{\crossHeight}[2]{(#1,#2) -- (#1,#2-1)}
% Relative to (a,b), with a in [0,1] and b in [0,3.5]:
% width  : x direction of the first crossing 
% height: y direction of the first crossing 
\newcommand{\crossWith}[3]{([xshift={#1*0.5mm+#2*0.375mm},yshift={#2*0.5mm-#3*0.125mm}]-0.05,0.09) 
    to[out=-82,in=164,looseness=1.7] 
    (0.05,0.09) to[out=164,in=-82,looseness=1.7] ([xshift={#1*0.5mm+#2*0.375mm-0.05},yshift={#2*0.5mm-#3*0.125mm}]-0.05,0.09)}
%
%... you can add more parameters if you need, like vertical position of the second crossing 
% (a b) : absolute vertical position
% (c,d) : relative position of the second crossing wrt the first one
\newcommand{\crossSecond}[4]{([xshift={#1*0.5mm+#2*0.125mm-0.05},yshift={#2*0.5mm-#3*0.125mm}]-0.05,0.09) 
    to[out=-82,in=164,looseness=1.7] 
    (0.05,0.09) to[out=164,in=-82,looseness=1.7] 
    ([xshift={#1*0.5mm+#2*0.125mm-0.05},yshift={#2*0.5mm-#3*0.125mm}]-0.05,0.09)}
%
% Automatically generate crossings:
% width  : x direction of the first crossing 
% height: y direction of the first crossing 
\newcommand{\crossAuto}[3]{%
\foreach \a in {0.3,0.8,1.3,1.8} {%
    \draw[blue,thick]
    (\crossWidth{\a}{\b}{\c})
    \crossWith{\a}{\b}{\c}%
    }
    \crossAuto{\c}{#1}{#2}%
}

