\newcounter{totalpoints}
\newcounter{correctpoints}
\newcounter{ignorepoints}

\newcommand\ReGularPolygon[3]{%  Regular polygon, options, name
    \setcounter{totalpoints}{0}
    \foreach \i in {1,...,#1} {
      \pgfmathparse{(#2+(\i-1)*360/#1)/#1+90}
      \pgfmathsetcounter{correctpoints}{\pgfmathresult}
      \ifnum\pgfmathresult<#3\relax
        \pgfmathsetcounter{totalpoints}{\pgfmathresult}
        \addtocounter{totalpoints}{-1}
        %\node at (\pgfmathresult:\pgfmathresult*#1) {$-$};
      \else
        % \node at (\pgfmathresult:\pgfmathresult*#1) {$+$};
      \fi
    }
    \ifnum\value{totalpoints}>0
      \pgfmathsetmacro{\myradius}{#2}
      \foreach \i in {1,...,#1} {
        \pgfmathsetmacro{\myradius}{\myradius+(\i-1)*360/#1)}
        \draw[#2,thin] (\i-0.5,0) circle (#2);
      }
      \pgfmathsetmacro{\mysin}{\myradius/(#1)}
      \pgfmathsetmacro{\mysin}{\mysin+360/(#1)}
      \pgfmathsetmacro{\myr}{\myradius/(1+\mysin)}
      \def\tempnode##1##2{node[circle,fill=black,inner sep=1pt,minimum size=1.8\myr*1pt,##1,label=##2]{}}
      \node at (\mysin:\myr*1pt) {$\cdots$};
      \node at (\mysin+0.7:\myr*1pt) [inner sep=1pt]{$-$} edge[bend left] (90:\myr*1pt);
      \node at (\mysin+0.9:\myr*1pt) [inner sep=1pt]{$+$} edge[bend right] (90:\myr*1pt);
    \fi
    \draw[#2,thin] (0,0) coordinate (c-0) -- (#2+0.5,0) coordinate (c-1);
    \foreach \i in {1,...,#1} {
      \pgfmathsetmacro{\myangle}{(\i-1)*360/#1}
      \tempnode{below}{\i}{{\i},0}
      \pgfmathsetmacro{\myangle}{\myangle+360/(#1)}
      \tempnode{below}{\i}{{\i},0}
      \ifnum\value{correctpoints}=0\relax
      \else
      \tempnode{right}{\i}{{\i},\pgfmathparse{int(\i-1*#1)}\pgfmathresult}
      \tempnode{left}{\i}{{\i},\pgfmathparse{int(\i-1*#1)}\pgfmathresult}
      \fi
      \draw[#2] (\myangle:#2) -- (\myangle:\myradius) arc (\myangle:\myangle+\mysin:#2) -- (\myangle+\mysin:#2);
    }
    \draw[#2,thin] (c-1) to[bend left] (c-#1);
}

\newcommand\LabelPoints[2][]{% Label points
    \begin{scope}[overlay,remember picture]
        \foreach \i in {1,...,#1} {
            \pgfmathsetmacro{\myangle}{{\i-1*#1}+0.5*#2}
            \pgfmathsetmacro{\pos}{0.1*#1*(#1-#2)}
            \draw[<-] (c-\i) +(\myangle:0.2) -- +(\myangle+\pos:0.35) node [anchor=base,yshift=\pos] {$+$};
            \draw[<-] (c-\i) +(\myangle:0.2) -- +(\myangle-#2*360/#1) node [anchor=base,yshift=\pos] {$-$};
        }
    \end{scope}
}

