\newcounter{toto}
\newcommand\MyNotes[4][\the\numexpr#2-1]%
% #1 = counter for "name"; can be omitted
% #2 = counter for "row"; #2-1 = name of row
% #3 = number of columns; #3-1 = index in [0,...,#2-1] for the "name"
% #4 = number of rows; #4-1 = index in [0,...,#3-1] for the "name"
\{%
\stepcounter{toto}
\if#1\relax
  \def\Position{0}
\else
  \def\Position{#1}
\fi
\setcounter{toto}{#4}%
\setcounter{toto}{#3}%
\def\tmpList{#2,#2,...,#4}%
\def\List{#2,#3,...,#4}%
\setcounter{toto}{0}%
\def\counter##1{#2}%
\foreach \Value in \tmpList
{
\pgfmathsetmacro{\ValueLabel}{\Value}%
\ifnum\Value<#2
  \ifnum\Value>\counter{toto}
    \node[point,fill=none,draw=red!40!black!80] at ($(\Position*#1,\Position*#2)+(0,0.5)$) {};
  \else
    \node[point,fill=none] at ($(\Position*#1,\Position*#2)+(0,0.5)$) {};
  \fi
    \node[point,fill=none] at ($(\Position*#1,\Position*#2-0.5)+(0,-0.6)$) {};
    \node[point,fill=none] at ($(\Position*#1-0.5,\Position*#2)+(0,-0.5)$) {};
  \else
    \node[point,fill=none,draw=red!40!black!80] at ($(\Position*#1,\Position*#2)+(0.5,0)$) {};
  \fi
\else
    \node[point,fill=none,draw=red!40!black!80] at ($(\Position*#1,\Position*#2)+(0,-0.5)$) {};
\fi
\setcounter{toto}{\Value}
\coordinate (\Value-Name) at ($(\Position*#1,\Position*#2)+(0,0.5)$);
\pgfmathsetmacro{\Position}{\Value}
}
}

