\newcommand{\hatch}[2]{
    \pgfmathparse{int(#1/2))*\hatchdistance}
    \pgfkeysalso{/pgfplots/area legend, /pgfplots/hatch pattern=vertical lines, hatch thickness=#2}
    \pgfplotsinvokeforeach {0,...,#1}{
        \pgfmathsetmacro{\mycolor}{mod(100*#1,100)}
        \pgfmathsetmacro{\myd}{1+2*#2}
        \path[
            draw=black,
            area legend,
            fill=\mycolor,
            postaction={
                draw=black,
                hatch distance=\hatchdistance,
                hatch thickness=\hatchthickness,
                line width=2pt+0.8*\hatchthickness
            }
        ] 
            plot[domain=0:900, samples=50, smooth, variable=\x, #1] 
            ({\x},{#2+30+50*exp(-0.06*\x)});
    }
}

\newcommand{\hatchline}[3]{
    \pgfmathparse{int(#1/2))*\hatchdistance}
    \draw[thin, red, /pgfplots/area legend, fill=white] 
        plot[domain=0:#2, samples=10, smooth] ({\x}, {#3});
    \draw[line width=2pt+0.8*\hatchthickness, green] 
        plot[domain=#2:0, samples=10, smooth] ({\x}, {#3});
}

