% \square
% \mysize
\newcommand\square[2]%
{%
  \draw[fill=white, draw=white,ultra thick] (-#1/2,-#1/2) rectangle +(#1,#1);
}

% \rectangle
% \mysize
\newcommand\rectangle[2]%
{%
  \draw[ultra thick,rounded corners=1.5*#1] (-#1/2,-#1/2) rectangle +(#1,#1);
  \draw[white,fill=white] (-#1/2-1.5*#1,#1/2-1.5*#1) rectangle +#1,#1-2*#1); 
}

% \circles
% \mysize
\newcommand\circles[2]%
{%
  \foreach\i in {1,...,#1}
  {%
    \node[fill=black,circle,inner sep=2pt] (c#2_\i) at (\i*#1/2,\i*#1/2) {};
  }
}

% \transpositions
% \mysize
\newcommand\transpositions[1]%
{%
  \foreach\j [count=\i from 1] in {#1}
  {%
    \coordinate (x) at ($2*(c#2_1_\i)+(2*#2,#2)$);
    \coordinate (y) at ($2*(c#2_1_\j)+(2*#2,#2)$);
    \draw[ultra thick,blue, ->, transform canvas={xshift=2cm}] (c#2_1_\i) -- (x);
    \draw[ultra thick,blue, ->, transform canvas={yshift=2cm}] (c#2_1_\i) -- (y);
  }
}

% \transpositions_plus
% #1 = number of columns of the grid to shift
% #2 = number of rows of the grid to shift
\newcommand\transpositions_plus[2]%
{%
  \begin{scope}[xshift=3cm, transform shape, shift={(0,0)}]
    \foreach\i [count=\j from 0, count=\k from 0, evaluate=\j as \t using \j+#1*0.5] in {#2}
    {%
      \node[shift={(-2cm,-2cm)},ultra thick, circle, draw=green, inner sep=0.5pt] (c1_\i) at (\t) {};
      \node[shift={( 2cm, 2cm)},ultra thick, circle, draw=green, inner sep=0.5pt] (c3_\i) at (\t) {};
    }
    \foreach\i [count=\j from 0, evaluate=\j as \t using \j] in {#1}
    {%
      \node[shift={( 2cm,-2cm)},ultra thick, circle, draw=green, inner sep=0.5pt] (c3_\i) at (\t) {};
      \node[shift={(-2cm, 2cm)},ultra thick, circle, draw=green, inner sep=0.5pt] (c1_\i) at (\t) {};
    }
  \end{scope}
}

% \equal
% \mysize
\newcommand\equal[2]%
{%
  \node (a) at ($#1+(#2-1)*0.5*(c#2_1_1)$) {#1};
  \node (b) at ($#1+#2*(c#2_1_1)$) {#2};
}

