\usetikzlibrary{calc}

\newcommand\rect[3]{%
  \draw[blue, very thick] (#1,#2) rectangle +(#3,#3);
}

\newcommand\permutation[5]{%
  \foreach \x in {1,...,#1} {
    \foreach \y in {1,...,#2} {
      \coordinate (n-\x-\y) at ($(\x-\y,#1+\y)$);
    }
  }
  \foreach \y in {1,...,#3} {
    \foreach \x in {1,...,#4} {
      \draw[very thick, red!25] ($(n-\x-\y)!0.5!(n-\x-\y -| n-\y-\y)$) -- +(0,1);
    }
  }
}

\newcommand\horizontalPermutation[5]{%
  \foreach \x in {1,...,#1} {
    \coordinate (n-\x-\y) at ($(\x-\y,#1 + \y)$);
  }
  \foreach \y in {1,...,#3} {
    \foreach \x in {1,...,#4} {
      \draw[very thick, blue!25] ($(n-\x-\y)!0.5!(n-\x-\y -| n-\y-\y)$) -- +(0,1);
    }
  }
}

