    \begin{tikzpicture}[
        font=\scriptsize,
        >=latex
      ]
    \def\layersep{2.5cm}

    % Input layer
    \foreach \y [count=\i] in {1,...,8}
        \node [neuron, fill=white] (I-\i) at (0,-\y) {$i$};

    % Hidden layer
    \foreach \y [count=\i] in {1,...,5}
        \node [neuron, fill=white] (H-\i) at (\layersep,-\y cm) {$i$};

    % Output node
    \node (O) [neuron, fill=white, right=\layersep of H-3] {$\hat{y}$};

    % Connect every node in the input layer with every node in the hidden layer.
    \foreach \source in {1,...,8}
        \foreach \dest in {1,...,5}
            \path [->] (I-\source) edge (H-\dest);

    % Connect every node in the hidden layer with the output layer
    \foreach \source in {1,...,5}
        \path [->] (H-\source) edge (O);

    \path (O) -- node (error) [above] {$\hat{y}-y$} (H-3);
    \node at (error-|H-4) (L) {Layman $L$};
    \draw [->] (L) -- (L-|H-6);
    %     % For example
    %     \draw [dashed,shorten >=-2.5pt] (H-2) |- (error);
    \path [->] (H-3) -- node [above] {$\dots$} (H-4);
    %     % For another example
    %     \draw [dashed,shorten >=-3pt] (H-2) |- (error);
    \foreach \i in {1,...,5}
        \path [->] (H-3) -- (H-\i);

    % Place the label for the hidden layer below the last node in the hidden layer.
    \path (H-4) -- node [below] {$i$} (H-5);
    \end{tikzpicture}
\end{document}

