\begin{tikzpicture}

% generate 5noisy GRNs
% set number of chains
\pgfmathsetmacro{\nch} {8} % number of chains

% set number of nodes in each GRN
\pgfmathsetmacro{\nU}{8}
\pgfmathsetmacro{\nV}{16}
\pgfmathsetmacro{\nW}{25}
\pgfmathsetmacro{\nw}{\nW - 4}
\pgfmathsetmacro{\nH}{16}

% set number of plates in each GRN
\pgfmathsetmacro{\nplate}{4}
\pgfmathsetmacro{\nG}{4}
\pgfmathsetmacro{\nsplit}{2}

% draw nodes in GRN (in order)
\foreach \i in {1,...,\nU}{
    \node[draw,circle,inner sep=0] (u\i) at ({(0.5 * \i) - 0.5}, -0.25) {$ $};
}
\foreach \i in {2,...,\nV}{
    \pgfmathparse{0.9 - 0.95*((\i - \nV)/\nV};
    \node[draw,circle,inner sep=0] (v\i) at ({0.5*(\i - 1) - 0.5}, -1.25) {$ $};
}
\foreach \i in {1,...,\nW}{
    \node[draw,circle,inner sep=0] (w\i) at ({0.5*(\i - 1) - 0.5}, -2.25) {$ $};
}
\foreach \i in {1,...,\nw}{
    \pgfmathparse{0.9 - 0.95*((\i - 1) - \nch/\nch)};

    \node[draw,circle,inner sep=0] (x\i) at ({(0.5 * \i) - 0.5}, -2.5 - 0.9*\i) {$ $};
}

% draw edges in GRN (in order)
\foreach \i in {1,...,\nU}{
    \pgfmathparse{0.95 - 0.9*(\i - 1)/\nU};

    \foreach \j in {1,...,\nV}{
        \foreach \k in {1,...,\nW}{
            \pgfmathparse{0.5 / (\nU + 1) * (\nV - \i + 1) * (\nW - \j + 1) * (\nW - \k + 1)}; % this is the probability with which the two nodes are connected
            % draw an edge between the two nodes if the probability is greater than 0.5
            \ifdim\pgfmathresult pt > 0.5pt
                \path (u\i) edge[-,thick] (v\j);
                \path (x\i) edge[-,thick] (x\k);
            \fi
        }
    }

    \foreach \j in {1,...,\nV}{
        \pgfmathparse{0.5 / (\nV + 1)}; % compute the number of nodes to connect to on the right
        \node[above of=u\i, yshift=-2.5] {$G_{\j}$};
        \path (x\i.east) edge[-,thick] (x\j.west); % connect the nodes on the right
    }
}

% draw plates in GRN
\foreach \i in {1,...,\nplate}{
    \pgfmathparse{((\i - 1) - \nch)/ \nplate};
    \node[draw,circle,inner sep=0] (plate\i) at ({(0.5 * \i) - 0.5}, -3.5 -  \i * 1.5) {$ $};
}
% draw connections between plates
\foreach \i in {1,...,\nplate}{
    \path (plate\i.north) edge[-,thick] (plate\i.east);
    \path (plate\i.south) edge[-,thick] (plate\i.east);
}
\path (x1.south) edge[-,thick] (u1.north);
\path (x4.south) edge[-,thick] (u1.north);
\path (x1.west) edge[-,thick] (u1.east);
\path (x4.west) edge[-,thick] (u1.east);
\path (x3.east) edge[-,thick] (u1.west);

% draw names of GINs
\node[align=center] (tgh0) at (-2.5, 1.5) {\textbf{TSG:} $H_{0}$ \\ $G_{0}$};
\node[align=center] (tgh1) at (-2.5, -0.5) {\textbf{TSG:} $H_{1}$ \\ $G_{1}$};
\node[align=center] (tgh2) at (-2.5, -3) {\textbf{TSG:} $H_{2}$ \\ $G_{2}$};

% draw names of plates
\node[align=center] (plateG0) at (-4.5, 0.5) {\textbf{$G_{0}$}};
\node[align=center] (plateG1) at (-4.5, -1) {\textbf{$G_{1}$}};
\node[align=center] (plateG2) at (-4.5, -2.5) {\textbf{$G_{2}$}};

% draw names of U nodes
\node[align=center] (u0) at (-6, 0.5) {$I_{0}$};
\node[align=center] (u1) at (-6, -1) {$I_{1}$};
\node[align=center] (u2) at (-6, -2.5) {$I_{2}$};

% draw names of V nodes
\node[align=center] (v0) at (-8, -1) {$H_{0}$};
\node[align=center] (v1) at (-8, -2.5) {$H_{1}$};

% draw names of W nodes
\node[align=center] (w1) at (-10, -2.5) {$P_{0}$};
\node[align=center] (w2) at (-10, -0.5) {$P_{1}$};

% draw names of W nodes
\node[align=center] (x0) at (-11.5, -0.5) {$H_{0}$};
\node[align=center] (x1) at (-11.5, -1) {$H_{1}$};
\node[align=center] (x2) at (-11.5, -2.5) {$H_{2}$};
\node[align=center] (x3) at (-11.5, -3) {$\cdots$};

