\setlength{\hstep}{5pt}   % vertical spacing between the graphs

% The \graph command
% #1: options for the outer layer
% #2: options for the inner layer
% #3: the degree to which the tree is to be drawn
% #4: number of the outer layer
% #5: number of the inner layer
\newcommand{\graph}[5][semithick,black,opacity=1]{
    \begin{scope}[
        yshift=-5cm,
        shift={(#4 cm,-5cm)},
        outer sep=0pt,
        inner sep=0pt,
    ]
    \ifnum #3=0
    \else
    \foreach \n in {1,...,#3}{
        \pgfmathtruncatemacro{\angle}{360/#3*\n}
        \ifthenelse{\n=#5}
        {\draw[#2] (\angle-#2*30:0.7) -- (\angle-#2*30:1.2);} % else: draw connection
        {\draw[#2] (\angle-#2*30:0.7) -- (\angle-#2*30:1.2) node[anchor={(\angle-#2*30>180? 180+360/#3+90 : 90 )}] {C_\n};} % else: draw connection
    }
    \fi
    \fi
    \begin{scope}[
        yshift=0cm,
        shift={(#5 cm,-5cm)},
        outer sep=0pt,
        inner sep=0pt,
    ]
    \graph[#1]{#4}{#3}{#2}{#4}
    \end{scope}
    \end{scope}
}

% The \drawall command
% #1: options for the outer layer
% #2: options for the inner layer
% #3: number of the outer layer
% #4: number of the inner layer
\newcommand{\drawall}[5][semithick,black,opacity=1]{
    \graph[#1, draw=black, opacity=0.5] {#4}{#3}{#2}{#4}
    \graph[#1, draw=black, opacity=0.5] {#5}{#3}{#2}{#5}
    \begin{scope}[
        yshift=-5cm,
        shift={(#4 cm,-5cm)},
        outer sep=0pt,
        inner sep=0pt,
    ]
    \foreach \n in {1,...,#3}{
        \pgfmathtruncatemacro{\angle}{360/#3*\n}
        \ifthenelse{\n=#5}
        {\draw[#2] (\angle-#2*30:0.7) -- (\angle-#2*30:1.2);} % else: draw connection
        {\draw[#2] (\angle-#2*30:0.7) -- (\angle-#2*30:1.2) node[anchor={(\angle-#2*30>180? 180+360/#3+90 : 90 )}] {C_\n};} % else: draw connection
    }
    \fi
    \end{scope}
    \begin{scope}[
        yshift=0cm,
        shift={(#5 cm,-5cm)},
        outer sep=0pt,
        inner sep=0pt,
    ]
    \graph[#1]{#4}{#3}{#2}{#4}
    \foreach \n in {1,...,#3}{
