\begin{tikzpicture}
% establish some constants:
\newcommand*{\numofcities}{8}% number of cities
\newcommand*{\radius}{0.4}% radius of the torii gate
\newcommand*{\numofsum}{8}% number of summations
\begin{scope}[local bounding box=graph]
\foreach \x in {1,...,\numofcities}{
    % randomly place the cities:
    \pgfmathsetmacro{\cityradius}{\radius+rand/3}
    \pgfmathtruncatemacro{\sumx}{\numofsum*2*\x}
    \pgfmathtruncatemacro{\sumy}{\numofsum*2*\cityradius-rand/3+1}
    \node[draw,circle] (city\x) at (\sumx,\sumy) {\x};
    \ifthenelse{\x<\numofcities}{
        \draw[thick] (city\x.north) -- (city\the\numexpr\x+1\relax.south);
    }{
        \draw[thick] (city\x.north) -- ({\numofsum*2*\cityradius+rand/3},\radius);
    }
}
\foreach \x in {1,...,\numofsum}{
    % randomly place the summations:
    \pgfmathsetmacro{\y}{\numofsum*2*rand/(\numofsum-1)}
    \pgfmathtruncatemacro{\cityid}{\numofcities-1+\x}
    \node[draw,fill=white] (sum\x) at (\x*\radius,\y*\radius) {};
    \ifthenelse{\x<\numofsum}{
        \draw[thick] (sum\the\numexpr\x+1\relax) -- (sum\cityid);
    }{
        \pgfmathsetmacro{\c}{(\x-1)*360/\numofsum*45}
        \draw[thick] (sum\the\numexpr\x+1\relax) -- (\c:0.5*\radius+2*\y)
            arc(\c:\c+360/\numofsum:0.5*\radius+2*\y) -- (sum\cityid);
    }
}
\end{scope}
% draw the torii gate:
\foreach \x in {1,...,\numofcities}{
    \draw[thick] (city\the\numexpr\x-0.5\relax) -- (city\the\numexpr\x-0.5\relax|-tori)
        -- (city\the\numexpr\x+0.5\relax|-tori) -- (city\the\numexpr\x+0.5\relax);
    \draw[thick] (city\the\numexpr\x-0.5\relax|-tori) -- (sum\the\numexpr\x-0.5\relax);
    \draw[thick] (city\the\numexpr\x+0.5\relax|-tori) -- (sum\the\numexpr\x+0.5\relax);
}
\foreach \x in {1,...,\numofcities}{
    \pgfmathtruncatemacro{\y}{int(mod(\x, \numofcities)+1)}
    \pgfmathtruncatemacro{\y}{\numofcities-\y}
    \pgfmathtruncatemacro{\y}{\numofsum-\y}
    \ifthenelse{\x=\numofcities}{
        \draw[thick] (city\the\numexpr\x-0.5\relax) -- (city\y|-tori)
            -- (city\the\numexpr\x+0.5\relax|-tori) -- (city\y+1.5*rand/3.5|-tori) -- (sum\y+1.5*rand/3.5);
        \draw[thick] (sum\the\numexpr\x-0.5\relax) -- (sum\y);
        \draw[thick] (tori) -- (sum\y+1.5*rand/3.5);
    }{
        \pgfmathsetmacro{\c}{(\x-1)*360/\numofsum*45}
        \draw[thick] (city\the\numexpr\x-0.5\relax) -- (\c:0.5*\radius+2*\y)
            arc(\c:\c+360/\numofsum:0.5*\radius+2*\y) -- (sum\y);
        \draw[thick] (tori) -- (sum\y+1.5*rand/3.5);
    }
}
\end{tikzpicture}
