    \foreach \i in {1,...,10} {
        \node (n-\i) at (60*\i:5) {};
    }
  \end{scope}

  \node[below=10 of paper] (G) at (paper.south) {$G$};
  \node[below=5 of G] (T) at (T.south) {$T$};

  \begin{scope}[shift={(paper.south west)},x=(G.south east),y=(T.north west)]
    % first the nodes
    \foreach \i in {1,...,10} {
      \node (g\i) at (60*\i:2.5) {};
    }
    \foreach \i in {3,...,10} {
      \pgfmathparse{Mod(\i-8,4)? "black" : "white"}
      \edef\colour{\pgfmathresult}
      \node[draw=\colour,inner sep=5mm] (n\i) at ($(g\i)+(0,0.5)$) {};
    }
    % connections
    \foreach \i in {3,...,9} {
      \foreach \j in {\i,...,10} {
        \draw [semithick,->] (g\i) to (n\j);
      }
    }
    % the box
    \draw [semithick]
      (n10.north west) to [bend right] ($(n9.north) + (-0.5,0.5)$)
      ($(n1.south east) + (0.5,-0.5)$) to [bend left] (n10.south east);
  \end{scope}

  \begin{scope}[shift={(paper.south west)},x=(G.south east),y=(T.north west), local bounding box=graph]
    % first the nodes
    \foreach \i in {1,...,10} {
        \node (n\i) at (60*\i:3) {};
    }
    % connections
    \foreach \i in {3,...,10} {
      \foreach \j in {\i,...,10} {
        \draw [semithick,->] (n\i) to (n\j);
      }
    }
  \end{scope}

  \begin{scope}[shift={(paper.south west)},x=(G.south east),y=(T.north west), local bounding box=graph2]
    % first the nodes
    \foreach \i in {1,...,10} {
        \node (n\i) at (60*\i:4.5) {};
    }
    % connections
    \foreach \i in {1,...,10} {
      \foreach \j in {\i,...,10} {
        \draw [semithick,->] (n\i) to (n\j);
      }
    }
  \end{scope}

  \begin{scope}[shift={(paper.south west)},x=(G.south east),y=(T.north west), local bounding box=graph3]
    % first the nodes
    \foreach \i in {1,...,10} {
        \node (n\i) at (60*\i:5.5) {};
    }
    % connections
    \foreach \i in {3,...,10} {
      \foreach \j in {\i,...,10} {
        \draw [semithick,->] (n\i) to (n\j);
      }
    }
  \end{scope}

  \node[below=3mm of graph.south west,anchor=north west] {$\mathcal{G}(T)$};
  \node[below=3mm of graph2.south west,anchor=north west] {$\mathcal{G}(T)$\'$};
  \node[below=3mm of graph3.south west,anchor=north west] {$\mathcal{G}(T)$\’\’};
