               to[bend right=45] coordinate[pos=.2] (aux1)
               coordinate[pos=.8] (aux2) (#1:#2)
              coordinate[pos=0]  (aux3);
   \draw[move left=\out,shorten >=2pt] (aux1) -- (aux3);
   \draw[move right=\in,shorten >=2pt] (aux2) -- (aux3);
}

