\newcommand{\LinearGraph}[2]{%
\begin{tikzpicture}[box/.style={rectangle,draw=gray, minimum width=3em}]
  \foreach \num/\lab [count=\c] in {#2} {
    \node (n\c) at ($(0,\c*(#1)-1)+(1.5,0)$) {$\num$};
    \node[box, label=right:{$\lab$}] (t\c) at (0, \c*(#1)-1) {};
  }
  \foreach \c in {1,...,#1} {
    \draw[-, shorten >=-#1] (n\c.north west) -- (t\c.south west);
    \draw[-, shorten >=-#1] (n\c.north east) -- (t\c.south east);
  }
\end{tikzpicture}%
}

