% for dot-free and increment-free updates
\newcommand\basiceval[1]{%
  \begingroup
  \pgfmathsetmacro{\inStep}{#1/10}%
  \pgfmathsetmacro{\lStep}{#1/100}%
  \edef\basiceval{\noexpand\xdef\theLSTy{\noexpand\theLSTy}}%
  \foreach\i in {0,...,10}%
  {%
    \expandafter\basiceval\theLSTy\i\relax
    \advance\i\basiceval
  }%
  \endgroup
}%
\newcommand\inneryval[1]{%
  \begingroup
  \tikzset{%
    /utils/exec=%
    {%
      \pgfmathsetmacro{\val}{\theLSTy}
      \expandafter\basiceval\theLSTy
      \expandafter\pgfmathsetmacro\csval{\val/10}%
      %
      \def\val{#1}%
      \ifx\val\csval%
      \else%
        \expandafter\def\csval\expandafter{\expandafter\val}%
      \fi%
      \expandafter\pgfmathsetmacro\csval{\csval+\val}%
    }%
  }%
  \tikzset{%
    insert path={%
      node[midway] (c#1) {\theLSTy}
      node[below=0 of c#1] (p#1) {\noexpand\tikzset{%
        /utils/exec=\noexpand#1%
      }}%
      node[below=0 of p#1] (d#1) {\theLSTy}%
      (c#1.north east) edge[thick,red!75] (p#1.south west)
      (c#1.north west) edge[thick,red!75] (p#1.south east)
    }%
  }%
}
% for drawing square grids (\i*10 \times \i*10 grid)
\pgfmathdeclarefunction{SqGDI}{2}{%
