\newcommand\Div[3]%
% 1: name of node
% 2: number of dividers
% 3: value to be displayed above the node
{%
  \begin{scope}[#1]
    \foreach \num in {1,...,#2}%
    {%
      \coordinate (div\num) at ($(-\num*\num mm,0) + #1$);
    }
    \draw (div1) -- (div#2);
    \pgfmathsetmacro{\half}{#3/2}
    \foreach \i in {2,...,#2}%
    {%
      \pgfmathtruncatemacro{\j}{\i-1}%
      \draw ($(-\i*\i mm,-\half*mm) + #1$) -- ($(-\j*\j*\num mm,-\half*mm) + #1$) node [midway, anchor=base] {$\frac{1}{\num}$};
    }
    \draw[red] ($(div#2)+(0,0.25mm)$) -- ($(div#2)+(0,-0.25mm)$);
    \draw[blue] ($(-0.5*\num mm,0.5mm) + #1$) -- ($(-0.5*\num mm,-0.5mm) + #1$);
    \draw[green] ($(-0.25*\num mm,0.75mm) + #1$) -- ($(-0.25*\num mm,-0.75mm) + #1$);
  \end{scope}
}

\newcommand\Divs[3]%
% 1: name of node
% 2: number of dividers
% 3: value to be displayed above the node
{%
  \begin{scope}[#1]
    \foreach \num in {1,...,#2}%
    {%
      \node[inner sep=0] (div\num) at ($(\num*\num mm,0) + #1$) {\Div{\num}{#2}{#3}};
    }
  \end{scope}
}

