%calculate n*(n+1)/2 for the labeling of the hypercube faces
\newcommand{\n}{6}

\begin{document}
\begin{tikzpicture}
\pgfmathsetmacro{\nplusone}{\n+1}
\pgfmathsetmacro{\angle}{360/\n}
\pgfmathsetmacro{\initialangle}{90+(\angle/2)(1-Mod(\n,2))}
% draw the edges of the hypercube
\foreach \i in {1,2,...,\nplusone} {
    \pgfmathsetmacro{\x}{(1-Mod(\i,2)))*\angle}
    \pgfmathsetmacro{\y}{\angle*\i}
    \draw[thick] (\y-1:\r) -- (\y:\r);
}

% draw the hypercube faces
\foreach \i in {1,2,...,\n}{
    \pgfmathsetmacro{\x}{(1-Mod(\i,2)))*\angle}
    \pgfmathsetmacro{\y}{\angle*\i}
    \fill[black,thick] (\y-1:\r) arc (\y-1:\y:\r) -- (\y:\r) arc (\y:\x:\r) -- (\x:\r) arc (\x:\y:\r) -- cycle;
}

% label the hypercube faces
\foreach \i in {1,2,...,\nplusone} {
    \pgfmathsetmacro{\x}{(1-Mod(\i,2)))*\angle}
    \pgfmathsetmacro{\y}{\angle*\i}
    \path (\y-1:\r) node[anchor=180+\y*\angle] {$\ifthenelse{\i=\nplusone}{4}{\i}$};
}

%label the edges
\foreach \i in {1,2,...,\nplusone} {
    \pgfmathsetmacro{\x}{(1-Mod(\i,2)))*\angle}
    \pgfmathsetmacro{\y}{\angle*\i}
    \path (\y-1:\r) node[anchor=180+\y*\angle] {$\i$};
}
\foreach \i in {1,2,...,\nplusone} {
    \pgfmathsetmacro{\x}{(1-Mod(\i,2)))*\angle}
    \pgfmathsetmacro{\y}{\angle*\i}
    \path (\y:\r+.1) node[anchor=180+\y*\angle] {$\i$};
}
\end{tikzpicture}
\end{document}

