\begin{document}
\begin{tikzpicture}[line cap=round,line join=round, x=0.7cm, y=0.7cm]
  \def\N{5}
  % groups
  \foreach\i in {1,...,\N}
  {% group nodes
    \pgfmathsetmacro\first{30-30*\i}
    \pgfmathsetmacro\second{30-30*\i+30}
    \node (group\i) at (\first:4) [circle,draw=black,fill=white] {\i};
  }
  % arrows
  \foreach\i in {1,...,\N}
  {% first arrows
    \pgfmathtruncatemacro\group   {int(Mod(\i-2,\N)+1)}
    \pgfmathtruncatemacro\secondgroup{int(Mod(\i-1,\N)+1)}
    \draw[myarrow] (group\i) -- node [above,sloped] {$x_{\i}$} (group\group);
    \draw[myarrow,red] (group\i) -- node [below,sloped] {$y_{\i}$} (group\secondgroup);
  }
  % second arrows
  \foreach\i in {1,...,\N}
  {% second arrows
    \pgfmathtruncatemacro\group   {int(Mod(\i-1,\N)+1)}
    \pgfmathtruncatemacro\secondgroup{int(Mod(\i-2,\N)+1)}
    \draw[myarrow,red] (group\i) -- node [above,sloped] {$x_{\i}$} (group\secondgroup);
    \draw[myarrow] (group\i) -- node [below,sloped] {$y_{\i}$} (group\secondgroup);
  }
  % vertical arrows
  \foreach\i in {1,...,\N}
  {% vertical arrows
    \pgfmathtruncatemacro\j{\i+1}
    \draw[myarrow,red] (group\i.east) -- (group\j);
  }
  % transformation
  \pgfmathsetmacro\d{0.7*3.141592654}
  \draw[myarrow,olive] (group1.north) + (90:\d) arc (90:150:\d);
  \draw[myarrow,olive] (group2.north) + (90:\d) arc (90:210:\d);
  \draw[myarrow,olive] (group3.north) + (90:\d) arc (90:270:\d);
  % nodes
  \foreach\i in {1,...,\N}
  {% nodes in groups
    \node (node\i) at ($0.5*(group\i.south east)+0.5*(group\i.south west)$) {$\cdot$};
    \node at ($0.5*(group\i.north east)+0.5*(group\i.north west)$) {$\cdot$};
  }
  % lines between nodes
  \foreach\i/\j in {1/2,2/3,3/4}
  {% lines between groups
    \draw[myarrow] (node\i.east) -- node [above] {$x_{\i}$} (node\j.west);
    \draw[myarrow] (node\i.south) -- node [below] {$y_{\i}$} (node\j.north);
  }
  % connections
  \foreach\i in {1,...,\N}
  {% connections
    \pgfmathtruncatemacro\j{\i+1}
    \draw[myarrow] (node\i.east) |- ($(node\j.west)+(0,0.1)$);
    \draw[myarrow] (node\j.west) -| ($(node\i.east)+(0.1,0)$);
  }
  % labels
  \node (zero) at ($(group1.north west)+(0,0.5)$) {$0$};
  \node (one) at ($(group1.north east)+(0.5,0.5)$) {1};
  \node (two) at ($(group2.north west)+(0,0.5)$) {2};
  \node (three) at ($(group2.north east)+(0.5,0.5)$) {3};
  \node (four) at ($(group3.north west)+(0,0.5)$) {4};
  \node at ($(group3.north east)+(0.5,0.5)$) {5};
  \node at (group1.east  +:0.1) {6};
  \node at (group2.east  +:0.1) {$\cdot$};
  \node at (group3.east  +:0.1) {$\cdot$};
  \node at (group1.west -:0.1) {$\cdot$};
  \node at (group2.west -:0.1) {$\cdot$};
  \node at (group3.west -:0.1) {$\cdot$};
\end{tikzpicture}
\end{document}

