\newcommand{\lattice}[1]{%
    \begin{scope}[shift={(#1)}]
    \foreach \n in {0,...,3}{
        \foreach \i in {-1,...,3}{
            \pgfmathparse{0.5*ifthenelse(sqrt(\n/2+\i/2) < 2.3,1,0)}
            \ifnum \pgfmathresult=1
                \path[fill=myllama] (\i,-\n) rectangle ++(1,-1);
            \else
                \path[fill=myllama] (\i,-\n) rectangle ++(-2,-1);
            \fi
            \ifnum \i=0
                \path[fill=myviolett] (\i,-\n) rectangle ++(1,-1);
            \else
                \path[fill=myviolett] (\i,-\n) rectangle ++(-1,-1);
            \fi
            \ifnum \n=0
                \path[fill=myindigo] (\i,-\n) rectangle ++(1,-1);
            \else
                \path[fill=myindigo] (\i,-\n) rectangle ++(-1,-1);
            \fi
        }
    }
    \end{scope}}

\newcommand{\pic}(s,option)= {%
