	\clip (0,0) circle (2.0);
	\pgfmathsetmacro{\rad}{1.5};
	\pgfmathsetmacro{\d}{4};

	\foreach \i in {1,...,\d} {
		\draw[thick] ({360/\d * (\i - 1)}:\rad) -- ({360/\d * (\i - 1)}:{\rad + 1.2}) node at ({360/\d * (\i + 0.5)}:{\rad + 1.2}) {$\hat K_{\i}$};
	}

	\foreach \i in {1,...,\d} {
		\draw[thick] ({360/\d * (\i - 1)}:\rad) -- ({360/\d * (\i)}:\rad);
	}

	\foreach \i in {1,...,\d} {
		\pgfmathsetmacro{\k}{\d-\i};

		\draw[thick] ({360/\d * (\i - 1)}:\rad) -- ({360/\d * (\k - 1)}:\rad);
	}

	\foreach \i in {1,...,\d} {
		\pgfmathsetmacro{\k}{\d-\i};

		\draw[thick] ({360/\d * (\i - 1)}:\rad) -- ({360/\d * (\k - 1)}:\rad);
	}

	\foreach \i in {1,...,\d} {
		\pgfmathsetmacro{\k}{\d-\i};

		\draw[thick] ({360/\d * (\i - 1)}:{\rad + 1.2}) -- ({360/\d * (\k - 1)}:{\rad + 1.2});
	}

	\pgfmathsetmacro{\d}{5};

	\foreach \i in {1,...,\d} {
		\draw[thick,fill=black] ({360/\d * (\i - 1)}:\rad) circle (.03);
	}

	\foreach \i in {1,...,\d} {
		\pgfmathsetmacro{\k}{\d-\i};

		\draw[thick,fill=white] ({360/\d * (\i - 1)}:\rad) circle (.03);
		\draw[thick,fill=black] ({360/\d * (\k - 1)}:\rad) circle (.03);
	}

	\foreach \i in {1,...,\d} {
		\pgfmathsetmacro{\k}{\d-\i};

		\draw[thick,fill=black] ({360/\d * (\i - 1)}:{\rad + 1.2}) circle (.03);
		\draw[thick,fill=black] ({360/\d * (\k - 1)}:{\rad + 1.2}) circle (.03);
	}

	\pgfmathsetmacro{\d}{6};

	\foreach \i in {1,...,\d} {
		\pgfmathsetmacro{\k}{\d-\i};

		\draw[thick,fill=black] ({360/\d * (\i - 1)}:{\rad + 2.0}) circle (.03);
		\draw[thick,fill=black] ({360/\d * (\k - 1)}:{\rad + 2.0}) circle (.03);
	}

	\foreach \i in {1,...,\d} {
		\pgfmathsetmacro{\k}{\d-\i};

		\draw[thick,fill=black] ({360/\d * (\i - 1)}:{\rad + 2.0}) circle (.03);
		\draw[thick,fill=white] ({360/\d * (\k - 1)}:{\rad + 2.0}) circle (.03);
	}

	\pgfmathsetmacro{\d}{3};

	\foreach \i in {1,...,\d} {
		\pgfmathsetmacro{\k}{\d-\i};

		\draw[thick,fill=black] ({360/\d * (\i - 1)}:{\rad + 2.8}) circle (.03);
		\draw[thick,fill=black] ({360/\d * (\k - 1)}:{\rad + 2.8}) circle (.03);
	}

	\foreach \i in {1,...,\d} {
		\pgfmathsetmacro{\k}{\d-\i};

		\draw[thick,fill=black] ({360/\d * (\i - 1)}:{\rad + 2.8}) circle (.03);
		\draw[thick,fill=white] ({360/\d * (\k - 1)}:{\rad + 2.8}) circle (.03);
	}

	\pgfmathsetmacro{\d}{1};

	\foreach \i in {1,...,\d} {
		\pgfmathsetmacro{\k}{\d-\i};

		\draw[thick,fill=black] ({360/\d * (\i - 1)}:{\rad + 3.6}) circle (.03);
		\draw[thick,fill=white] ({360/\d * (\k - 1)}:{\rad + 3.6}) circle (.03);
	}

	\end{scope}
\end{tikzpicture}

