\pgfmathsetmacro{\yc}{\yd*sin(60) - \yd/2} % vertical position of center of hexagon
\pgfmathsetmacro{\t}{2*sqrt(3)/3} % thickness of hexagon
\foreach \i in {1,...,#2}
{%
  \pgfmathsetmacro{\x}{\xc + \t*\i} % x coordinate of next vertex
  \pgfmathsetmacro{\y}{\yc + \t*\i} % y coordinate of next vertex
  \filldraw[#3] (\x,\y) -- (\x+\t/2,\y+\t) -- (\x+\t,\y+\t) -- (\x,\y+\t+\t) -- cycle;
  \coordinate (c) at ($(\x,\y)+(60:\t)$);
  \coordinate (d) at ($(\x,\y)+(60*2:\t)$);
}
}

\newcommand{\squar}[2] % outer dimensions, color
{%
\pgfmathsetmacro{\xd}{#1/2} % half width of hexagon
\pgfmathsetmacro{\yd}{sqrt(#1/3)} % half height of hexagon
\filldraw[#1] (0,0) rectangle (\xd,\yd);
}

