\newcommand\irreducible[1]{%
\begin{tikzpicture}[#1]%
    \node[circle,draw] (a) at (1,1) {#1};
    \node[draw,inner sep=2pt] (b) at (1,0) {};%
    \node[draw,inner sep=2pt] (c) at (1,-1) {};%
    \node[draw,inner sep=2pt] (d) at (0,-1) {};%
    \node[draw,inner sep=2pt] (e) at (2,-1) {};%
    \node[draw,inner sep=2pt] (f) at (3,-1) {};%
    \foreach \i/\j in {a/b,b/c,b/d,c/e,d/f}
    {
        \draw[->] (\i) -- (\j);
    }
    \draw[->,dashed] (e) -- (f);
    \draw (1.5,-1.5) node {#1};
\end{tikzpicture}
}

