\Tree
[.{\small\sffamily The Treasury}
    [.{\small\sffamily $M \leftarrow \emptyset$}
    [.{\small\strut $T_E^i$ sends $(BS_{i},Sig_{BS_{i}},EP_{BS_{i}})$} ]
    [.{\small\strut $T_S^i$ sends $(BS_{i},Sig_{BS_{i}})$}
    [.{\small\strut $T_{T}^i$ computes $d_{BS_{i}} \leftarrow EP_{BS_{i}}$ and $d_{BS_{i}} \oplus 1$} ]
    [.{\small\strut $M' \leftarrow$ \textsc{Hdak}$(d_{BS_{i}})$} ]
    [.{\small\strut $BS_i \leftarrow \arg \max_{1 \leq i \leq N}{log\left(\frac{M'}{N} \right)}$} ]
    [.{\small\strut $T_i\leftarrow $ \textsc{Hdak}$(d_{BS_{i}})$} ]
]
[.{$EP_{BS_{i}}$ is added to $T_E^i$'s collection of ephemeral keys (EP$_{BS_{i}}$)} ]
[.{$BS_{i} \in \{1,2, \cdots N\}$ is added to $T_S^i$'s list of BSs} ]
[.{$M' \leftarrow \arg \max_{1 \leq i \leq N}{log\left(\frac{M'}{N} \right)}$} ]
[.{$d_{BS_{i}}$ is added to Treasury's collection of ephemeral keys (EP$_{BS_{i}}$)} ]
]
]
